import NavBar from "./Components/NavBar";
import { Switch, Route } from 'react-router-dom';
import Seminars from "./pages/Seminars";
import Users from "./pages/Users";
import Teams from "./pages/Teams";
import SeminarType from "./pages/SeminarType";
import SeminarDetails from "./pages/SeminarDetails";
import UserDetails from "./pages/UserDetails";
import './App.css'
import Footer from "./Components/Footer";
import Manager from "./pages/Manager";
import { useState } from "react";
import { useHistory } from "react-router-dom";



function App() {
  let history = useHistory();
  const [email,setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [singnUp , setSingnUp] = useState(false);


  let login = false;
  if(sessionStorage.getItem('myData'))
  {
    login = true;
  }


  const [isLogin , setIsLogin] = useState(login)

  // --------------------------- SESSIONS MANAGE -----------------------------------------

  function setToken() {
    sessionStorage.setItem('myData' , true);
    setIsLogin(true)
    window.location.reload(false);

  }
  function getToken() {
    let data = sessionStorage.getItem('myData')
    console.log(data)

  }
  function deletToken() {
    sessionStorage.removeItem('myData');
    setIsLogin(false)
  }



  //   -----------------------------------------
  const emailHandler = (event) => {
    setEmail(event.target.value)
  }

  const passwordHandler = (event) => {
    setPassword(event.target.value)
  }


  const submitHandler = (event) => {
    event.preventDefault();
    console.log(email , password)

    if(email === 'jimarass@hotmail.gr' && password === '123456')
    {
      setToken()
      setPassword('');
      setEmail('');
      history.push("/");
      window.location.reload();
    }
    else{

      alert("Icorect password or Email")
      setPassword('');
      setEmail('');
    }


 }






  return (

    <div>

    <div className='wraper'>


       <NavBar ></NavBar>
       <Footer/>
       {isLogin && (
       <div>
       <Switch>
         <Route exact path='/'>

           <Seminars />
         </Route>
         <Route exact path='/users'>
           <Users />
         </Route>
         <Route exact path='/teams'>
           <Teams />
         </Route>
         <Route exact path='/seminartype'>
           <SeminarType />
         </Route>
         <Route exact path='/seminar/:id'>
           <SeminarDetails />
         </Route>
         <Route exact path='/users/:id'>
           <UserDetails />
         </Route>
         <Route exact path='/users/manager/:id/:last_name'>
           <Manager/>
         </Route>
       </Switch>
       </div> 
       )}

       {!isLogin && ( 
         <div> 
           <br/>

         <div class="container" id="container">

          {singnUp && ( 
           <div class="form-container sign-in-container">
             <form action="#">
               <h1>Create Account</h1>

               <span>or use your email for registration</span>
               <input type="text"  placeholder="Name" />
               <input type="email"  placeholder="Email" />
               <input type="password" placeholder="Password" />
               <button >Sign Up</button>
             </form>
           </div> 
           )}

           <div class="form-container sign-in-container">
             <form action="#" onSubmit={submitHandler}>
               <h1>Sign in</h1>
               <span>or use your account</span>
               <input onChange={emailHandler} value={email} type="email" placeholder="Email" />
               <input onChange={passwordHandler} value={password} type="password" placeholder="Password" />
               <a href="#">Forgot your password?</a>
               <button >Sign In</button>
             </form>
           </div>

           <div class="overlay-container">
             <div class="overlay">
               <div class="overlay-panel overlay-left">
                 <h1>Welcome Back!</h1>
                 <p>To keep connected with us please login with your personal info</p>
                 <button class="ghost" id="signIn">Sign In</button>
               </div>
               <div class="overlay-panel overlay-right">
                 <h1>Hello, COSMOTE!</h1>
                 <p>Enter your personal details and start journey with us</p>
                 <button  class="ghost" id="signUp">Sign Up</button>
               </div>
             </div>
           </div>
         </div>
         </div>

       )}




    </div>

    </div>

  );
}

export default App;
