import React from 'react'
import './NavBar.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faBars} from '@fortawesome/free-solid-svg-icons'
import { Link } from 'react-router-dom'
import { useState } from 'react'
import { useHistory ,useLocation } from 'react-router-dom';


const NavBar = () => {

  //const [seminar , setSeminar] = useState('active');
  //const [users, setUsers] = useState('')
  //const [team ,setTeam ] = useState('')
  //const [type ,  setType] = useState('')

  var activeNavBar = [];

  const location = useLocation()
  console.log(location.pathname)

  if(location.pathname === '/' )
    {
      /*setSeminar('active')
      setUsers('')
      setTeam('')
      setType('')*/
      for (let i = 0; i < 4; i++) {
        activeNavBar[i] = ''   
      }
      activeNavBar[0] = 'active'

    }
    if(location.pathname === 1 )
    {
      /*setSeminar('')
      setUsers('active')
      setTeam('')
      setType('')*/

    }
    if(location.pathname === 2 )
    {
      /*setSeminar('')
      setUsers('')
      setTeam('active')
      setType('')*/

    }
    if(location.pathname === 3)
    {
      /*setSeminar('')
      setUsers('')
      setTeam('')
      setType('active')*/

    }
  
  
  
  // --------------------NavBar Active-----------------
  

  const activeSeminarHandler = (num) => {
    
    /*
    if(num === 0 )
    {
      setSeminar('active')
      setUsers('')
      setTeam('')
      setType('')

    }
    if(num === 1 )
    {
      setSeminar('')
      setUsers('active')
      setTeam('')
      setType('')

    }
    if(num === 2 )
    {
      setSeminar('')
      setUsers('')
      setTeam('active')
      setType('')

    }
    if(num === 3)
    {
      setSeminar('')
      setUsers('')
      setTeam('')
      setType('active')

    }
    */

  }




    //-------------------------- Responsive Function ------------------------------
    function myFunction() {
        var x = document.getElementById("myTopnav");
        if (x.className === "topnav") {
          x.className += " responsive";
        } else {
          x.className = "topnav";
        }
      }

    return (
        
            <div>
                <div className="topnav" id="myTopnav">
                    
                    <Link className={seminar} onClick={()=> activeSeminarHandler(0)} to="/" >Seminars</Link>
                    <Link className={users} onClick={()=> activeSeminarHandler(1)} to="/users">Users</Link>
                    <Link className={team}  onClick={()=> activeSeminarHandler(2)} to="/teams">Teams</Link>
                    <Link className={type}  onClick={()=> activeSeminarHandler(3)} to="/seminartype">Type</Link>
                    <a  className="icon" onClick={myFunction} >
                    <FontAwesomeIcon icon={faBars} />
                    </a>
                </div>


            </div>
        
    )
}

export default NavBar
