import { useEffect, useState } from "react";
import popupStyles from './custom-popup.module.css';
import PropTypes from "prop-types";


const CustomPopup = (props) => {
    const [show, setShow] = useState(false);

    const closeHandler = (e) => {
        setShow(false);
        props.onClose(false);
    };

    useEffect(() => {
        setShow(props.show);
    }, [props.show]);

    return (
        <div 
        style={{ visibility: show ? "visible" : "hidden", opacity: show ? "1" : "0"}}
        className={popupStyles.overlay}>
         <div className={popupStyles.popup}>
             <h2>{props.title}</h2>
             <span className={popupStyles.close} onClick={closeHandler}>&times;</span>

            <form className={popupStyles.formad}>
             <label className={popupStyles.labelad}>Seminar Name: </label><br/>
            <input  className = {popupStyles.inputad} type="text" /><br/>

             <label className={popupStyles.labelad}>Organization:<br/></label><br/>
             <input  type="text" /><br/>

             <label className={popupStyles.labelad}>Type Of Seminar:<br/></label><br/>
             <input type="text" /><br/>

            <button className='buttonad' type='submit' >Add Seminar</button>

            </form>

             <div className={popupStyles.content}>{props.children}</div>
         </div>
        </div>
    );

 }; 

 CustomPopup.propTypes = {
    title: PropTypes.string.isRequired,
    show: PropTypes.bool.isRequired,
    onClose: PropTypes.func.isRequired
  };
  

  export default CustomPopup;
