import React, { Fragment,  useState } from 'react'
import { useParams } from 'react-router'
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import ToolkitProvider, { Search } from 'react-bootstrap-table2-toolkit';
import './UserDetails.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.css';
import 'react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css';
import 'react-calendar/dist/Calendar.css';
import Spiner from '../Components/Spiner';
import '../Components/Spiner.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faPlusSquare} from '@fortawesome/free-solid-svg-icons'
import {faSquare} from '@fortawesome/free-solid-svg-icons'
import { useHistory } from 'react-router';
import PopUpUserDetails from '../Components/PopUpUserDetails';
import moment from "moment";



import Calendar from 'react-calendar';



const UserDetails = () => {

  
    const [visibility, setVisibility] = useState(false);
    const [isLoading, setIsLoading] = useState(true)
    

    setTimeout( () => { setIsLoading(false) }, 500);

    let { id } = useParams();
    let {first_name} =useParams();
    //-----------FECH ALL DATA to 

    const userdetails = [
        {id: 1 , seminar: "Dynatrace 3 Modules (Monitoring Tools, APIs, Dashboard & Reporting Tools", duration: 30, starting_date: "1/7/2021" },
        {id: 2 , seminar: "seminar2", duration: 15, starting_date: "1/7/2021" },
        {id: 3 , seminar: "seminar3", duration: 20, starting_date: "15/5/2020" },
        {id: 4 , seminar: "seminar4", duration: 7, starting_date: "15/5/2020" },
        {id: 5 , seminar: "seminar5", duration: 20, starting_date: "15/5/2020"},
        {id: 6 , seminar: "seminar6", duration: 30, starting_date: "3/10/2019" },
        {id: 7 , seminar: "seminar7", duration: 30, starting_date: "1/7/2021" },
        {id: 8 , seminar: "seminar8", duration: 30, starting_date: "15/5/2020 "},
        {id: 9 , seminar: "seminar9", duration: 30, starting_date: "1/7/2021" },
        {id: 10 , seminar: "seminar10", duration: 30, starting_date: "3/10/2019" },
        {id: 11 , seminar: "seminar2", duration: 15, starting_date: "1/7/2021" },
        {id: 12 , seminar: "seminar3", duration: 20, starting_date: "15/5/2020" },
        {id: 13 , seminar: "seminar4", duration: 7, starting_date: "15/5/2020 "},
        {id: 14 , seminar: "seminar5", duration: 20, starting_date: "1/7/2021"},
        {id: 15 , seminar: "seminar6", duration: 30, starting_date: "15/5/2020" },
        {id: 16 , seminar: "seminar7", duration: 30, starting_date: "15/5/2020" },
        {id: 17 , seminar: "seminar8", duration: 30, starting_date: "1/7/2021" },
        {id: 18 , seminar: "seminar9", duration: 30, starting_date: "15/5/2020" },
        
      ];


    // ----------------SOME DUMMY DATES --------------------------   
    const vacation = [ { start:"2021-12-15" , finish: "2021-12-17" } , { start:"2021-12-30" , finish: "2022-1-2" } ]
    const standBy =  [ { start:"2022-01-7" , finish: "2022-01-10" } , { start:"2022-02-1" , finish: "2022-02-10" } ] 
         

     

      // ----------------------- RANGE MAKER FUNCTION -------------
      const rangeMaker = (startDate , finishDate) => {

      let array = []
       var currentDate = moment(startDate);
      var stopDate = moment(finishDate);
      while (currentDate <= stopDate) {
          array.push( moment(currentDate).format('YYYY-MM-DD') )
          currentDate = moment(currentDate).add(1, 'days');
      }
      return(array)    

  }


   
  // ----------------------- CTEATE ARRAYS OF VACATIONS , STANDBY... -------------------------
  var Vacations = [];
  for (let i = 0; i < vacation.length; i++) {
      
      Vacations = Vacations.concat(rangeMaker( vacation[i].start , vacation[i].finish))
      
  }
  


  var StandBy = [];
  for (let i = 0; i < standBy.length; i++) {
      
      StandBy = StandBy.concat(rangeMaker( standBy[i].start , standBy[i].finish))
      
  }
  

  // ----------------------- CONVERT DATE TO D-M-Y -----------------------
  function convertDate(inputFormat) {
    function pad(s) { return (s < 10) ? '0' + s : s; }
    var d = new Date(inputFormat)
    return [d.getFullYear(),pad(d.getMonth()+1) , pad(d.getDate()),  ].join('-')
  }  

   // ------------------------ On CLICK DAY -------------------------

   const doSomething = (value,event) =>{
    if(StandBy.includes(convertDate(value)) )
    {
        console.log("StandBy")
    }
    if(Vacations.includes(convertDate(value)))
    {
        console.log("Vacations")
    }

    
}
      
      


      let history = useHistory();

      const columns = [
        {dataField: 'id', text:'Id', sort: true, hidden:true},
        {dataField: 'seminar', text:'Seminar', sort: true,
        events: {
            onClick: (e, column, columnIndex, row, rowIndex) => {
              console.log(row);
              history.push(`/seminar/${row.id}`)
            }
          } 
    },
        {dataField: 'duration', text:'Duration', sort: true, searchable:false },
        {dataField: 'starting_date', text:'Starting_date', sort: true,  },
        
      ]

      
    
      const defaultSorted = [{
        dataField: 'id',
        order: 'asc'
      }];


      const pagination = paginationFactory({
        page: 1,
        sizePerPage: 5,
        lastPageText: '>>',
        firstPageText: '<<',
        nextPageText: '>',
        prePageText: '<',
        showTotal: true,
        alwaysShowAllBtns: true,
        onPageChange: function (page, sizePerPage) {
          console.log('page', page);
          console.log('sizePerPage', sizePerPage);
        },
        onSizePerPageChange: function (page, sizePerPage) {
          console.log('page', page);
          console.log('sizePerPage', sizePerPage);
        }
      });


      const { SearchBar, ClearSearchButton } = Search;

    //--------------------- Pop Up -------------------------
    const popupCloseHandler = () => {

        setVisibility(false);

      };

     const Array = []
     
    
    
    
    return (
        
      <Fragment>
        <FontAwesomeIcon className = 'boxV'   size= 'lg'   icon={faSquare}/> &nbsp; Vacation &nbsp; 
        <FontAwesomeIcon className = 'boxC'   size= 'lg'   icon={faSquare}/> &nbsp; Current Day &nbsp;
        <FontAwesomeIcon className = 'boxS'   size= 'lg'   icon={faSquare}/> &nbsp; Stand By &nbsp; 
        <FontAwesomeIcon className = 'boxT'   size= 'lg'   icon={faSquare}/> &nbsp; Training &nbsp;
        
          <div>
            { !isLoading && ( 
            <div> 
                <div className='Card'>
                    <h1>{'John'}</h1>
                    <h3>Last Name: Doe</h3>
                    <h3>Email: Test@test.com</h3>
                    <h3>Manager: Some Managers</h3> <br/>
                    <h1>Calendar</h1>
                    <Calendar className = 'Calendar' onClickDay = {(value, event ) => doSomething(value,event) }   tileClassName={ ({ date, view }) =>
            {

            
                
                if(view === "month" && StandBy.includes( moment(date).format("YYYY-MM-DD"))    )
                {
                    return("vocation");
                }
                if(Vacations.includes( moment(date).format("YYYY-MM-DD")))
                {
                    return("activeDay");
                }
                else{
                    return(null)
                }
                
            } 

            }   />
                <br></br>
                <div> The total days of <strong>Vacations</strong>  for this Year is : <strong>{Vacations.length}</strong> days</div>
                <div> The total days of <strong>StandBy</strong>  for this Year is :  <strong>{StandBy.length}</strong> days</div>
                
                </div>
                
                <div className='BootsTrapTable'>
                <div className='SearchBar'>
                    <ToolkitProvider
                        bootstrap4
                        keyField='id'
                        data={userdetails}
                        columns={columns}
                        search

                    >
                        {
                        props => (
                            <div>
                              Add a New User &nbsp; <FontAwesomeIcon className = 'buttonplus'  size= 'lg'  onClick={() => setVisibility(true)}  icon={faPlusSquare}/>
                            <hr />
                            <h6>Search for Seminars:</h6>
                            <SearchBar {...props.searchProps} />
                            <ClearSearchButton {...props.searchProps} />
                            <br/><br/>
                            
                            <BootstrapTable
                            
                                defaultSorted={defaultSorted}
                                pagination={pagination}
                                {...props.baseProps}
                            />
                            
                            </div>
                        )
                        }
                    </ToolkitProvider>

                 </div>
                    <PopUpUserDetails onClose={popupCloseHandler}  show={visibility} title="Add A New Seminar"> </PopUpUserDetails>
                    
                    <br/><br/><br/><br/><br/><br/><br/>
                </div>  
                
            </div> )} 
            {isLoading && <Spiner/>}

            
        </div>  
            
            
        </Fragment>
    )
}

export default UserDetails
