import React from "react";
import "./BigCategories.css";

import { useState, useEffect } from "react";
import { Col } from "react-bootstrap";

const BigCategories = ({ choice }) => {
  const [results, setResults] = useState([]);

  const getBigCategoriesResults = () => {
    let choiceDetails = {
      choice: choice,
    };
    fetch("http://localhost:3000/bigCaregories", {
      method: "POST",
      body: JSON.stringify(choiceDetails),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setResults(json);
      });
  };

  useEffect(() => {
    getBigCategoriesResults();
  }, []);

  return <div>BigCategories</div>;
};

export default BigCategories;
