import React, { useEffect } from "react";
import {
  Container,
  Col,
  Row,
  Button,
  Modal,
  Alert,
  Form,
} from "react-bootstrap";
import { useState } from "react";
import "./HelpUs.css";
import { FaPen, FaFish, FaPizzaSlice } from "react-icons/fa";
import { GiChickenOven } from "react-icons/gi";
import { TbCheese, TbChristmasTree } from "react-icons/tb";
import { GiEasterEgg } from "react-icons/gi";
import { BsFillSuitHeartFill } from "react-icons/bs";
import { ImSpoonKnife } from "react-icons/im";

const HelpUs = () => {
  const [writeFood, setWriteFood] = useState("");
  const [foodResults, setFoodResults] = useState([]);
  const [selectedFood, setSelectedFood] = useState("");

  // ------------ selected values -----------------
  const [colors, setColors] = useState([[], [], []]);
  const [types, setTypes] = useState([[], [], []]);
  const [grapes, setGrapes] = useState([[], [], []]);

  // ------------- drop Downs Values -----------------
  const [colorWines, setColorWines] = useState([]);
  const [typeWines, setTypeWines] = useState([]);
  const [yearWines, setYearWines] = useState([]);
  const [grapeWines, setGrapeWines] = useState([]);

  const getWines = () => {
    fetch("http://localhost:4000/getWines")
      .then((res) => res.json())
      .then((json) => {
        var colors = [];
        var types = [];
        var years = [];
        var prices = [];
        var grapes = [];
        for (const key in json.wines) {
          colors.push(json.wines[key].wine.Color);
          types.push(json.wines[key].wine.Type);
          years.push(json.wines[key].wine.Year);
          prices.push(json.wines[key].wine.Price);
          grapes.push(json.wines[key].wine.Grapes);
        }

        colors = [...new Set(colors)];
        types = [...new Set(types)];
        years = [...new Set(years)];
        grapes = [...new Set(grapes)];

        setColorWines(colors);
        setTypeWines(types);
        setYearWines(years);
        setGrapeWines(grapes);
      });
  };

  useEffect(() => {
    getWines();
  }, []);

  // ---------------- Food API Call --------------------
  const findAnyFood = (food) => {
    fetch(
      `https://api.spoonacular.com/recipes/complexSearch?apiKey=27e614b6300f46b38613b4bb811633d0&query=${food}`
    )
      .then((response) => response.json())
      .then((actualData) => {
        console.log(actualData.results);
        var foods = [];
        actualData.results.map((food) => {
          foods.push(food.title + "*" + food.id + "*" + food.image);
        });
        setFoodResults(foods);
      });
  };

  return (
    <div>
      <div className="title">
        <Row>
          <Col xs={9}>
            <h2>
              Your opinion matters &nbsp;
              <FaPen />
            </h2>
          </Col>
        </Row>
        <hr />
      </div>
      <div>
        <div className="helpUsTitle">
          <h5>&#8226; Big Categories</h5>
          <div className="helpUsTitleContent">
            <p>- Wine would you pair with : </p>

            <div className="helpUsTitleContent">
              <p>
                <strong>1.</strong> Red Meat{" "}
              </p>

              <Row>
                <Col xs={2}>
                  <Form.Label>Type</Form.Label>
                  <Form.Select size="sm">
                    {typeWines.map((type) => (
                      <option>{type}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Color</Form.Label>
                  <Form.Select size="sm">
                    {colorWines.map((color) => (
                      <option>{color}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Grape</Form.Label>
                  <Form.Select size="sm">
                    {grapeWines.map((grape) => (
                      <option>{grape}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Year Range</Form.Label>
                  <Form.Select size="sm">
                    <option>Small select</option>
                  </Form.Select>
                </Col>
              </Row>
            </div>

            <div className="helpUsTitleContent">
              <p>
                <strong>2.</strong> White Meat &nbsp;{" "}
                <GiChickenOven className="reactIcon" />{" "}
              </p>

              <Row>
                <Col xs={2}>
                  <Form.Label>Type</Form.Label>
                  <Form.Select size="sm">
                    {typeWines.map((type) => (
                      <option>{type}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Color</Form.Label>
                  <Form.Select size="sm">
                    {colorWines.map((color) => (
                      <option>{color}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Grape</Form.Label>
                  <Form.Select size="sm">
                    {grapeWines.map((grape) => (
                      <option>{grape}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Year Range</Form.Label>
                  <Form.Select size="sm">
                    <option>Small select</option>
                  </Form.Select>
                </Col>
              </Row>
            </div>

            <div className="helpUsTitleContent">
              <p>
                <strong>3.</strong> Fish &nbsp; <FaFish className="reactIcon" />{" "}
              </p>

              <Row>
                <Col xs={2}>
                  <Form.Label>Type</Form.Label>
                  <Form.Select size="sm">
                    {typeWines.map((type) => (
                      <option>{type}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Color</Form.Label>
                  <Form.Select size="sm">
                    {colorWines.map((color) => (
                      <option>{color}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Grape</Form.Label>
                  <Form.Select size="sm">
                    {grapeWines.map((grape) => (
                      <option>{grape}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Year Range</Form.Label>
                  <Form.Select size="sm">
                    <option>Small select</option>
                  </Form.Select>
                </Col>
              </Row>
            </div>

            <div className="helpUsTitleContent">
              <p>
                <strong>4.</strong> Cheece &nbsp;{" "}
                <TbCheese className="reactIcon" />{" "}
              </p>

              <Row>
                <Col xs={2}>
                  <Form.Label>Type</Form.Label>
                  <Form.Select size="sm">
                    {typeWines.map((type) => (
                      <option>{type}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Color</Form.Label>
                  <Form.Select size="sm">
                    {colorWines.map((color) => (
                      <option>{color}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Grape</Form.Label>
                  <Form.Select size="sm">
                    {grapeWines.map((grape) => (
                      <option>{grape}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Year Range</Form.Label>
                  <Form.Select size="sm">
                    <option>Small select</option>
                  </Form.Select>
                </Col>
              </Row>
            </div>

            <div className="helpUsTitleContent">
              <p>
                <strong>5.</strong> Pasta{" "}
              </p>

              <Row>
                <Col xs={2}>
                  <Form.Label>Type</Form.Label>
                  <Form.Select size="sm">
                    {typeWines.map((type) => (
                      <option>{type}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Color</Form.Label>
                  <Form.Select size="sm">
                    {colorWines.map((color) => (
                      <option>{color}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Grape</Form.Label>
                  <Form.Select size="sm">
                    {grapeWines.map((grape) => (
                      <option>{grape}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Year Range</Form.Label>
                  <Form.Select size="sm">
                    <option>Small select</option>
                  </Form.Select>
                </Col>
              </Row>
            </div>

            <div className="helpUsTitleContent">
              <p>
                <strong>6.</strong> Pizza &nbsp;{" "}
                <FaPizzaSlice className="reactIcon" />{" "}
              </p>

              <Row>
                <Col xs={2}>
                  <Form.Label>Type</Form.Label>
                  <Form.Select size="sm">
                    {typeWines.map((type) => (
                      <option>{type}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Color</Form.Label>
                  <Form.Select size="sm">
                    {colorWines.map((color) => (
                      <option>{color}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Grape</Form.Label>
                  <Form.Select size="sm">
                    {grapeWines.map((grape) => (
                      <option>{grape}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Year Range</Form.Label>
                  <Form.Select size="sm">
                    <option>Small select</option>
                  </Form.Select>
                </Col>
              </Row>
            </div>
          </div>
          <hr />
        </div>

        <div className="helpUsTitle">
          <h5>&#8226; Type Day</h5>
          <div className="helpUsTitleContent">
            <p>- Wine would you pair on : </p>

            <div className="helpUsTitleContent">
              <p>
                <strong>1.</strong> Christmas Day &nbsp;{" "}
                <TbChristmasTree className="reactIcon" />{" "}
              </p>

              <Row>
                <Col xs={2}>
                  <Form.Label>Type</Form.Label>
                  <Form.Select size="sm">
                    {typeWines.map((type) => (
                      <option>{type}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Color</Form.Label>
                  <Form.Select size="sm">
                    {colorWines.map((color) => (
                      <option>{color}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Grape</Form.Label>
                  <Form.Select size="sm">
                    {grapeWines.map((grape) => (
                      <option>{grape}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Year Range</Form.Label>
                  <Form.Select size="sm">
                    <option>Small select</option>
                  </Form.Select>
                </Col>
              </Row>
            </div>

            <div className="helpUsTitleContent">
              <p>
                <strong>3.</strong> Easter Day &nbsp;{" "}
                <GiEasterEgg className="reactIcon" />{" "}
              </p>

              <Row>
                <Col xs={2}>
                  <Form.Label>Type</Form.Label>
                  <Form.Select size="sm">
                    {typeWines.map((type) => (
                      <option>{type}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Color</Form.Label>
                  <Form.Select size="sm">
                    {colorWines.map((color) => (
                      <option>{color}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Grape</Form.Label>
                  <Form.Select size="sm">
                    {grapeWines.map((grape) => (
                      <option>{grape}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Year Range</Form.Label>
                  <Form.Select size="sm">
                    <option>Small select</option>
                  </Form.Select>
                </Col>
              </Row>
            </div>

            <div className="helpUsTitleContent">
              <p>
                <strong>3.</strong> Valentine Day &nbsp;{" "}
                <BsFillSuitHeartFill className="reactIcon" />{" "}
              </p>

              <Row>
                <Col xs={2}>
                  <Form.Label>Type</Form.Label>
                  <Form.Select size="sm">
                    {typeWines.map((type) => (
                      <option>{type}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Color</Form.Label>
                  <Form.Select size="sm">
                    {colorWines.map((color) => (
                      <option>{color}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Grape</Form.Label>
                  <Form.Select size="sm">
                    {grapeWines.map((grape) => (
                      <option>{grape}</option>
                    ))}
                  </Form.Select>
                </Col>
                <Col xs={2}>
                  <Form.Label>Year Range</Form.Label>
                  <Form.Select size="sm">
                    <option>Small select</option>
                  </Form.Select>
                </Col>
              </Row>
            </div>
          </div>
        </div>

        {/* 3 .  */}
        <hr />
        <div className="helpUsTitle">
          <h5>&#8226; Select your favourite food</h5>
          <div className="helpUsTitleContent">
            <p>
              - Select the food that you want to pair your wine{" "}
              <ImSpoonKnife className="reactIcon" />
            </p>

            <div className="helpUsTitleContent">
              <Row>
                <Col xs={3}>
                  <Form.Label>
                    Write&nbsp;&&nbsp;Find&nbsp;your&nbsp;Recipe
                  </Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Find any Food"
                    value={writeFood}
                    onChange={(e) => {
                      setWriteFood(e.target.value);
                      if (e.target.value.length > 2) {
                        findAnyFood(e.target.value);
                      }
                    }}
                  />
                </Col>
                <Col xs={3}>
                  <Form.Label>Recipe</Form.Label>
                  <Form.Select
                    onChange={(e) => {
                      setSelectedFood(e.target.value);
                    }}
                  >
                    {foodResults.length === 0 && (
                      <option value="">Food select</option>
                    )}
                    {foodResults.map((food) => (
                      <option value={food}>{food.split("*")[0]}</option>
                    ))}
                  </Form.Select>
                </Col>
              </Row>
              <br />
              <Row>
                <Col xs={6}>
                  {selectedFood !== "" && (
                    <>
                      <p>Image</p>
                      <img
                        className="imageFood"
                        src={selectedFood.split("*")[2]}
                      ></img>
                    </>
                  )}
                </Col>
              </Row>
            </div>
          </div>

          {selectedFood !== "" && (
            <div className="helpUsTitleContent">
              <p>
                - Wine would you pair with{" "}
                <strong>{selectedFood.split("*")[0]}</strong> :{" "}
              </p>

              <div className="helpUsTitleContent">
                <Row>
                  <Col xs={2}>
                    <Form.Label>Type</Form.Label>
                    <Form.Select size="sm">
                      <option>Small select</option>
                    </Form.Select>
                  </Col>
                  <Col xs={2}>
                    <Form.Label>Color</Form.Label>
                    <Form.Select size="sm">
                      <option>Small select</option>
                    </Form.Select>
                  </Col>
                  <Col xs={2}>
                    <Form.Label>Grape</Form.Label>
                    <Form.Select size="sm">
                      <option>Small select</option>
                    </Form.Select>
                  </Col>
                  <Col xs={2}>
                    <Form.Label>Year Range</Form.Label>
                    <Form.Select size="sm">
                      <option>Small select</option>
                    </Form.Select>
                  </Col>
                </Row>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default HelpUs;
