import React from "react";
import "./AddWine.css";
import { Container, Col, Row, Button } from "react-bootstrap";
import { useEffect, useState } from "react";
import Multiselect from "multiselect-react-dropdown";

const AddWine = () => {
  const allGrapes = [
    "Prosecco",
    "Malagouzia",
    "Asurtiko",
    "Xinomauro",
    "Sauvignon Blanc",
    "Mosxofilero",
    "Chardonnay",
    "Merlot",
    "Cabernet Sauvignon",
    "Mosxato",
    "Agiorgitiko",
    "Maurodafni",
    "Ponot Noir",
    "Roditis",
    "Pinot Grigio",
    "Malbec",
  ];
  const allTypes = ["Dry", "Semi-sweet", "Sweet", "Semi-dry"];
  const allColors = ["Red", "White", "Roze"];
  var allyears = [];
  for (let i = 1950; i < new Date().getFullYear() + 1; i++) {
    allyears.push(i);
  }

  const [wineName, setWineName] = useState("");
  const [Type, setType] = useState(allTypes[0]);
  const [color, setColor] = useState(allColors[0]);
  const [year, setYear] = useState(allyears[0]);
  const [grape, setGrape] = useState(allGrapes[0]);
  const [winery, setWinery] = useState("");
  const [price, setPrice] = useState("");
  const [country, setCountry] = useState("");
  const [location, setLocation] = useState("");
  const [discription, setDiscription] = useState("");
  const [imageURL, setImageURL] = useState("");

  const onClearHandler = () => {
    setWineName("");
    setType(allTypes[0]);
    setColor(allColors[0]);
    setYear(allyears[0]);
    setGrape(allGrapes[0]);
    setWinery("");
    setPrice("");
    setCountry("");
    setLocation("");
    setDiscription("");
    setImageURL("");
  };

  const onAddWineHandler = () => {
    console.log(
      wineName,
      Type,
      color,
      year,
      grape,
      winery,
      price,
      country,
      location,
      discription,
      imageURL
    );

    let wineDetails = {
        wineName: wineName,
        Type: Type,
        color: color,
        id: id,
      };
      fetch("http://localhost:4000/addWine", {
        method: "POST",
        body: JSON.stringify(wineDetails),
        headers: { "Content-Type": "application/json" },
      })
        .then((res) => res.json())
        .then((json) => {
          console.log(json);
         
        });
  };

  return (
    <div>
      <div className="mainDiv">
        <div className="cardStyle addWine">
          <h2 className="formTitle">Add Wine</h2>

          <Container>
            <Row>
              <Col xs={4}>
                <div className="inputDiv">
                  <label className="inputLabel">Wine Name</label>
                  <input
                    type="text"
                    id="password"
                    value={wineName}
                    onChange={(e) => {
                      setWineName(e.target.value);
                    }}
                  />
                </div>
              </Col>
              <Col xs={4}>
                <div className="inputDiv">
                  <label className="inputLabel">Winery</label>
                  <input
                    type="text"
                    id="password"
                    value={winery}
                    onChange={(e) => {
                      setWinery(e.target.value);
                    }}
                  />
                </div>
              </Col>
              <Col xs={4}>
                <div className="inputDiv">
                  <label className="inputLabel">Type</label>
                  <select className="selectGrape">
                    {allTypes.map((type) => (
                      <option
                        value={type}
                        onChange={(e) => {
                          setType(e.target.value);
                        }}
                      >
                        {type}
                      </option>
                    ))}
                  </select>
                </div>
              </Col>
            </Row>
            <Row>
              <Col xs={4}>
                <div className="inputDiv">
                  <label className="inputLabel">Color</label>
                  <select className="selectGrape">
                    {allColors.map((color) => (
                      <option
                        value={color}
                        onChange={(e) => {
                          setColor(e.target.value);
                        }}
                      >
                        {color}
                      </option>
                    ))}
                  </select>
                </div>
              </Col>
              <Col xs={4}>
                <div className="inputDiv">
                  <label className="inputLabel">Year</label>
                  <select className="selectGrape">
                    {allyears.reverse().map((years) => (
                      <option
                        value={years}
                        onChange={(e) => {
                          setYear(e.target.value);
                        }}
                      >
                        {years}
                      </option>
                    ))}
                  </select>
                </div>
              </Col>
              <Col xs={4}>
                <div className="inputDiv">
                  <label className="inputLabel">Price</label>
                  <input
                    type="text"
                    id="password"
                    value={price}
                    onChange={(e) => {
                      setPrice(e.target.value);
                    }}
                  />
                </div>
              </Col>
            </Row>
            <Row>
              <Col xs={4}>
                <div className="inputDiv">
                  <label className="inputLabel">Country</label>
                  <input
                    type="text"
                    id="password"
                    value={country}
                    onChange={(e) => {
                      setCountry(e.target.value);
                    }}
                  />
                </div>
              </Col>
              <Col xs={4}>
                <div className="inputDiv">
                  <label className="inputLabel">Location</label>
                  <input
                    type="text"
                    id="password"
                    value={location}
                    onChange={(e) => {
                      setLocation(e.target.value);
                    }}
                  />
                </div>
              </Col>
              <Col xs={4}>
                <div className="inputDiv">
                  <label className="inputLabel">Grapes</label>
                  <select className="selectGrape">
                    {allGrapes.map((grape) => (
                      <option
                        value={grape}
                        onChange={(e) => {
                          setGrape(e.target.value);
                        }}
                      >
                        {grape}
                      </option>
                    ))}
                  </select>
                </div>
              </Col>
            </Row>

            <Row>
              <Col xs={4}>
                {" "}
                <div className="inputDiv">
                  <label className="inputLabel">Discription</label>
                  <input
                    className="wineDiscription"
                    type="text"
                    id="password"
                    value={discription}
                    onChange={(e) => {
                      setDiscription(e.target.value);
                    }}
                  />
                </div>
              </Col>
              <Col xs={4}></Col>
              <Col xs={4}>
                {" "}
                <div className="inputDiv">
                  <label className="inputLabel">Image Url</label>
                  <input
                    type="text"
                    id="password"
                    value={imageURL}
                    onChange={(e) => {
                      setImageURL(e.target.value);
                    }}
                  />
                </div>
              </Col>
            </Row>
          </Container>
          <br />
          <Container>
            <Row>
              <Col xs={4}></Col>
              <Col xs={1}>
                {" "}
                <Button variant="danger" onClick={onClearHandler}>
                  &nbsp;&nbsp;Clear&nbsp;&nbsp;
                </Button>
              </Col>
              <Col xs={2}></Col>
              <Col xs={1}>
                {" "}
                <Button
                  variant="success"
                  onClick={onAddWineHandler}
                  disabled={
                    wineName == "" ||
                    winery === "" ||
                    setPrice.trim() === "" ||
                    setCountry.trim() === "" ||
                    setLocation.trim() === "" ||
                    setDiscription.trim() === "" ||
                    setImageURL.trim() === "" 
                   
                  }
                >
                  Add&nbsp;Wine
                </Button>
              </Col>
              <Col xs={3}></Col>
            </Row>
          </Container>

          <br />
          <br />
          <br />
          <br />
          <br />
        </div>
      </div>
    </div>
  );
};

export default AddWine;
