import React from "react";
import "./EditProfile.css";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
// import { Container } from "react-bootstrap";
import { Container, Row, Col } from "react-bootstrap";

const EditProfile = () => {
  const [user, setUser] = useState([]);
  const cookies = new Cookies();
  var userEmail = cookies.get("email");

  const getUser = (email) => {
    var emailForSearch = email === undefined ? userEmail : email;
    fetch(`http://localhost:4000/getUser/${emailForSearch}`)
      .then((res) => res.json())
      .then((json) => {
        setUser([json]);
        console.log([json]);
      });
  };

  useEffect(() => {
    getUser();
  }, []);
  return (
    <div>
      {user.length !== 0 && (
        <div className="mainDiv">
          <div className="cardStyle">
            <h2 className="formTitle">Edit your Profile</h2>

            <Container>
              <Row>
                <Col xs={6}>
                  <div className="inputDiv">
                    <label className="inputLabel">First Name</label>
                    <input
                      //value={user[]}
                      type="text"
                      id="password"
                    />
                  </div>
                </Col>
                <Col xs={6}>
                  {" "}
                  <div className="inputDiv">
                    <label className="inputLabel">Last Name</label>
                    <input
                      //value={newConfirmPassword}
                      type="text"
                      id="confirmPassword"
                    />
                  </div>
                </Col>
              </Row>

              <Row>
                <Col xs={6}>
                  <div className="inputDiv">
                    <label className="inputLabel">Email</label>
                    <input
                      //value={newPassword}
                      type="email"
                    />
                  </div>
                </Col>
                <Col xs={6}>
                  {" "}
                  <div className="inputDiv">
                    <label className="inputLabel">Mobile</label>
                    <input
                      //value={newConfirmPassword}
                      type="text"
                      id="confirmPassword"
                    />
                  </div>
                </Col>
              </Row>
            </Container>

            <div className="buttonWrapper">
              <button
                type="submit"
                id="submitButton"
                //onClick={onChangeHandler}
                className="submitButton pure-button pure-button-primary"
              >
                <span>Continue to Change</span>
              </button>
            </div>
            <br />
            <br />
            <br />
            <br />
            <br />
          </div>
        </div>
      )}
    </div>
  );
};

export default EditProfile;
