import React from "react";
import "./BigCategories.css";
import { Col, Row, Form, Button } from "react-bootstrap";
import { FaFish, FaPizzaSlice } from "react-icons/fa";
import { GiChickenOven } from "react-icons/gi";
import { TbMeat } from "react-icons/tb";
import { GiRopeCoil } from "react-icons/gi";
import { TbCheese } from "react-icons/tb";
import { useState, useEffect } from "react";

const BigCategories = ({ colorWines, typeWines, grapeWines, wineYears }) => {
  const [deviceSize, changeDeviceSize] = useState(window.innerWidth);
  useEffect(() => {
    const resizeW = () => changeDeviceSize(window.innerWidth);

    window.addEventListener("resize", resizeW); // Update the width on resize
    return () => window.removeEventListener("resize", resizeW);
  });

  console.log()
  const [redMeat, setReadMeat] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [whiteMeat, setWhiteMeat] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [fish, setFish] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [cheece, setCheece] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [pasta, setPasta] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [pizza, setPizza] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  return (
    <div>
      pair
      <div>
        <h5>&#8226; Big Categories</h5>
        <div className="helpUsTitleContent">
          <p>- Wine would you pair with : </p>

          <div className="helpUsTitleContent">
            <p>
              <strong>1.</strong> Red Meat <TbMeat className="reactIcon" />
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: e.target.value,
                      Color: redMeat.Color,
                      Grape: redMeat.Grape,
                      Year: redMeat.Year,
                    });
                  }}
                >
                  <option
                    value=""
                    selected={redMeat.Type === "" ? true : false}
                  >
                    Select Type
                  </option>
                  {typeWines.map((type) => (
                    <option selected={redMeat.Type === type ? true : false}>
                      {type}
                    </option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: redMeat.Type,
                      Color: e.target.value,
                      Grape: redMeat.Grape,
                      Year: redMeat.Year,
                    });
                  }}
                >
                  <option
                    value=""
                    selected={redMeat.Color === "" ? true : false}
                  >
                    Select color
                  </option>
                  {colorWines.map((color) => (
                    <option selected={redMeat.Color === color ? true : false}>
                      {color}
                    </option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: redMeat.Type,
                      Color: redMeat.Color,
                      Grape: e.target.value,
                      Year: redMeat.Year,
                    });
                  }}
                >
                  <option
                    value=""
                    selected={redMeat.Grape === "" ? true : false}
                  >
                    Select grape
                  </option>
                  {grapeWines.map((grape) => (
                    <option selected={redMeat.Grape === grape ? true : false}>
                      {grape}
                    </option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: redMeat.Type,
                      Color: redMeat.Color,
                      Grape: redMeat.Grape,
                      Year: e.target.value,
                    });
                  }}
                >
                  <option
                    value=""
                    selected={redMeat.Year === "" ? true : false}
                  >
                    Select year
                  </option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>2.</strong> White Meat &nbsp;{" "}
              <GiChickenOven className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setWhiteMeat({
                      Type: e.target.value,
                      Color: whiteMeat.Color,
                      Grape: whiteMeat.Grape,
                      Year: whiteMeat.Year,
                    });
                  }}
                >
                  <option
                    value=""
                    selected={whiteMeat.Type === "" ? true : false}
                  >
                    Select Type
                  </option>
                  {typeWines.map((type) => (
                    <option selected={whiteMeat.Type === type ? true : false}>
                      {type}
                    </option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setWhiteMeat({
                      Type: whiteMeat.Type,
                      Color: e.target.value,
                      Grape: whiteMeat.Grape,
                      Year: whiteMeat.Year,
                    });
                  }}
                >
                  <option
                    value=""
                    selected={whiteMeat.Color === "" ? true : false}
                  >
                    Select color
                  </option>
                  {colorWines.map((color) => (
                    <option selected={whiteMeat.Color === color ? true : false}>
                      {color}
                    </option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setWhiteMeat({
                      Type: whiteMeat.Type,
                      Color: whiteMeat.Color,
                      Grape: e.target.value,
                      Year: whiteMeat.Year,
                    });
                  }}
                >
                  <option
                    value=""
                    selected={whiteMeat.Grape === "" ? true : false}
                  >
                    Select grape
                  </option>
                  {grapeWines.map((grape) => (
                    <option selected={whiteMeat.Grape === grape ? true : false}>
                      {grape}
                    </option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setWhiteMeat({
                      Type: whiteMeat.Type,
                      Color: whiteMeat.Color,
                      Grape: whiteMeat.Grape,
                      Year: e.target.value,
                    });
                  }}
                >
                  <option
                    value=""
                    selected={whiteMeat.Year === "" ? true : false}
                  >
                    Select year
                  </option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>3.</strong> Fish &nbsp; <FaFish className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setFish({
                      Type: e.target.value,
                      Color: fish.Color,
                      Grape: fish.Grape,
                      Year: fish.Year,
                    });
                  }}
                >
                  <option value="" selected={fish.Type === "" ? true : false}>
                    Select Type
                  </option>
                  {typeWines.map((type) => (
                    <option selected={fish.Type === type ? true : false}>
                      {type}
                    </option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setFish({
                      Type: fish.Type,
                      Color: e.target.value,
                      Grape: fish.Grape,
                      Year: fish.Year,
                    });
                  }}
                >
                  <option value="" selected={fish.Color === "" ? true : false}>
                    Select color
                  </option>
                  {colorWines.map((color) => (
                    <option selected={fish.Color === color ? true : false}>
                      {color}
                    </option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setFish({
                      Type: fish.Type,
                      Color: fish.Color,
                      Grape: e.target.value,
                      Year: fish.Year,
                    });
                  }}
                >
                  <option value="" selected={fish.Grape === "" ? true : false}>
                    Select grape
                  </option>
                  {grapeWines.map((grape) => (
                    <option selected={fish.Grape === grape ? true : false}>
                      {grape}
                    </option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setFish({
                      Type: fish.Type,
                      Color: fish.Color,
                      Grape: fish.Grape,
                      Year: e.target.value,
                    });
                  }}
                >
                  <option value="" selected={fish.Year === "" ? true : false}>
                    Select year
                  </option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>4.</strong> Cheece &nbsp;{" "}
              <TbCheese className="reactIcon" />{" "}
            </p>

