import React from "react";
import "./Blog.css";
import {
  Button,
  Container,
  Row,
  Col,
  Form,
  Alert,
  OverlayTrigger,
  Popover,
} from "react-bootstrap";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
import { dateFormater } from "./Functions/functions";

const Blog = () => {
  const [blogs, setBlogs] = useState([]);
  const [user, setUser] = useState([]);
  const cookies = new Cookies();
  var userEmail = cookies.get("email");

  // --------- get All Blogs ----------------
  const getBlogs = () => {
    fetch(`http://localhost:4000/getBlogs`)
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setBlogs(json);
      });
  };

  useEffect(() => {
    getBlogs();
  }, []);

  const getUser = (email) => {
    setUser([]);
    fetch(`http://localhost:4000/getUser/${email}`)
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setUser([json]);
      });
  };

  const popover = (
    <Popover id="popover-basic">
      {user.length !== 0 && (
        <div>
          <Popover.Header as="h3">{u}</Popover.Header>
          <Popover.Body>
            And here's some <strong>amazing</strong> content. It's very
            engaging. right?
          </Popover.Body>
        </div>
      )}
    </Popover>
  );

  return (
    <div>
      <div className="container py-4">
        <div className="col-md-10 col-lg-8 m-auto">
          <div className="bg-white rounded-3 shadow-sm p-4">
            <h4 className="mb-4">Leave a Question</h4>

            <div className="alert alert-danger" role="alert"></div>

            <form action="/auth/blogAddQuestion" method="POST">
              <div className="d-flex">
                <img
                  className="rounded-circle me-3"
                  src="https://via.placeholder.com/128/{{this.randomColor}}/ffcbde.png?text={{this.StaringName}}"
                />
                <div className="flex-grow-1">
                  <div className="hstack gap-2 mb-1">
                    <a href="#" className="fw-bold link-dark"></a>
                  </div>
                  <div className="form-floating mb-3">
                    <textarea
                      className="form-control w-100"
                      placeholder="Leave a comment here"
                      id="my-comment"
                      name="question"
                      required
                    ></textarea>
                    <label>Leave a message here</label>
                  </div>

                  <div className="hstack justify-content-end gap-2">
                    <button className="btn btn-sm btn-link link-secondary text-uppercase">
                      cancel
                    </button>
                    <button
                      className="btn btn-sm btn-primary text-uppercase"
                      type="submit"
                    >
                      comment
                    </button>
                  </div>
                </div>
              </div>
            </form>

            <br />
            <hr />
            <h4 className="mb-4">Questions</h4>

            <div></div>
            <br />
            <br />
            <hr />

            <div className="">
              <div className="py-3">
                {blogs.length !== 0 && (
                  <div className="table-wrapper">
                    {blogs.map((blog) => (
                      <div className="d-flex comment">
                        <img
                          className="rounded-circle me-3"
                          src={`https://via.placeholder.com/130/${Math.floor(
                            Math.random() * 16777215
                          ).toString(16)}/ffcbde.png?text=${
                            blog.UserName.split(" ")[0][0] +
                            blog.UserName.split(" ")[1][0]
                          }`}
                        />
                        <div className="flex-grow-1 ms-3">
                          <div className="mb-1">
                            <OverlayTrigger
                              trigger="click"
                              placement="right"
                              overlay={popover}
                            >
                              <Button
                                onClick={() => {
                                  getUser(blog.UserEmail);
                                }}
                                className="popoverButton"
                              >
                                {blog.UserName}
                              </Button>
                            </OverlayTrigger>
                            {/* <a
                              href="/blogInfo/{{this._id}}"
                              className="fw-bold link-dark me-1"
                            >
                              {blog.UserName}
                            </a>{" "} */}
                            <br />{" "}
                            <span className="text-muted text-nowrap">
                              {dateFormater(new Date(blog.QuestionDate))}
                            </span>
                          </div>
                          <div className="mb-2">{blog.Question}</div>
                          <div className="hstack align-items-center mb-2">
                            <a className="link-primary me-2" href="#">
                              <i className="zmdi zmdi-thumb-up"></i>
                            </a>
                            <span className="me-3 small">
                              <strong></strong> aplies.
                            </span>
                            <a className="link-secondary me-4" href="#">
                              <i className="zmdi zmdi-thumb-down"></i>
                            </a>

                            {blog.UserEmail === userEmail && (
                              <button
                                className="btn btn-danger"
                                title="Delete Question!"
                              >
                                Delete
                              </button>
                            )}

                            <div
                              className="modal fade"
                              id="exampleModal"
                              role="dialog"
                              aria-labelledby="exampleModalLabel"
                              aria-hidden="true"
                            >
                              <div className="modal-dialog" role="document">
                                <div className="modal-content">
                                  <div className="modal-header">
                                    <h5
                                      className="modal-title"
                                      id="exampleModalLabel"
                                    >
                                      Modal title
                                    </h5>
                                    <button
                                      type="button"
                                      className="close"
                                      data-dismiss="modal"
                                      aria-label="Close"
                                    >
                                      <span aria-hidden="true">&times;</span>
                                    </button>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
                          <a
                            className="fw-bold d-flex align-items-center"
                            href="#"
                          >
                            <i className="zmdi zmdi-chevron-down fs-4 me-3"></i>
                          </a>
                        </div>
                      </div>
                    ))}
                  </div>
                )}

                <br />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Blog;
