import React from "react";
import "./Blog.css";
import { Button, OverlayTrigger, Popover } from "react-bootstrap";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
import { dateFormater, getAge } from "./Functions/functions";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faReply } from "@fortawesome/free-solid-svg-icons";

const Blog = () => {
  const [blogs, setBlogs] = useState([]);
  const [user, setUser] = useState([]);
  const [question, setQuestion] = useState("");
  const [userName, setUserName] = useState("");
  const [userId, setUserId] = useState("");
  const cookies = new Cookies();
  var userEmail = cookies.get("email");

  // --------- get All Blogs ----------------
  const getBlogs = () => {
    fetch(`http://localhost:4000/getBlogs`)
      .then((res) => res.json())
      .then((json) => {
        console.log([json]);
        setBlogs([json]);
      });
  };

  useEffect(() => {
    getBlogs();
    getUser(userEmail);
  }, []);

  const getUser = (email) => {
    setUser([]);
    fetch(`http://localhost:4000/getUser/${email}`)
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setUser([json]);
        setUserName(json.user.Name);
        setUserId(json.user._id);
      });
  };

  const popover = (
    <Popover id="popover-basic">
      {user.length !== 0 && (
        <div>
          <Popover.Header as="h3">{user[0].user.Name}</Popover.Header>
          <Popover.Body>
            Email : <strong>{user[0].user.Email}</strong> <br />
            Tel. : <strong>{user[0].user.Mobile}</strong> <br />
            Address : <strong>{user[0].user.Address}</strong> <br />
            Age : <strong>
              {getAge(new Date(user[0].user.Birthday))}
            </strong>{" "}
            <br />
          </Popover.Body>
        </div>
      )}
    </Popover>
  );

  const onCreateQuestion = () => {
    console.log(question, userEmail, userId, userName);

    let blogDetails = {
      email: userEmail,
      id: userId,
      Name: userName,
      question: question,
    };
    fetch("http://localhost:4000/createQuestion", {
      method: "POST",
      body: JSON.stringify(blogDetails),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setQuestion("");
        getBlogs();
      });
  };

  return (
    <div className="blogAndFilter">
      <div className='applieToQuestionFilters'>
        <h4>Search for comments</h4>
        <h4>&nbsp; Search any wine.</h4>

            <Container>
              <Row>
                <Form>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Filter Wine</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Filter Wine"
                      value={wineSearch}
                      onChange={(e) => {
                        setWineSearch(e.target.value);
                      }}
                    />
                  </Form.Group>
                 
                 
                  <Form.Label>Wine Year</Form.Label>
                  <Form.Select
                    aria-label="Default select example"
                    onChange={(e) => {
                      setWineYear(e.target.value);
                    }}
                  >
                    <option value="">2000/2010/2020..</option>
                   
                      <option value="{year}">ljkjkj</option>
                  
                  </Form.Select>
                  <br />
                  <Row>
                    <Col xs={6}>
                      <Form.Label>Min Rate Range</Form.Label>
                      <Form.Range
                        min="0"
                        max="5"
                       
                        onChange={(e) => {
                          if (maxRate > e.target.value) {
                            setMinRate(e.target.value);
                          }
                        }}
                      />
                      <strong>
                        {minRate}
                        <FontAwesomeIcon
                          className="starIcon"
                          icon={faStar}
                          size="1x"
                        />
                      </strong>
                    </Col>

                    <Col xs={6}>
                      {" "}
                      <Form.Label>Max Rate Range</Form.Label>
                      <Form.Range
                        min="0"
                        max="5"
                        value={maxRate}
                        onChange={(e) => {
                          if (minRate < e.target.value) {
                            setMaxRate(e.target.value);
                          }
                        }}
                      />
                      <strong>
                        {maxRate}
                        <FontAwesomeIcon
                          className="starIcon"
                          icon={faStar}
                          size="1x"
                        />
                      </strong>
                    </Col>
                  </Row>
                  <br />
                  <Row>
                    <Col xs={6}>
                      <Form.Label>Min Price Price</Form.Label>
                      <Form.Range
                        min={minInitialPrice}
                        max={maxInitialPrice}
                        value={
                          minPrice === minInitialPrice
                            ? minInitialPrice
                            : minPrice
                        }
                        onChange={(e) => {
                          if (maxPrice > e.target.value) {
                            setMinPrice(e.target.value);
                          }
                        }}
                      />
                      <strong>{minPrice} </strong>
                    </Col>
                    <Col xs={6}>
                      {" "}
                      <Form.Label>Max Price Price</Form.Label>
                      <Form.Range
                        min={minInitialPrice}
                        max={maxInitialPrice}
                        value={
                          maxPrice === maxInitialPrice
                            ? maxInitialPrice
                            : maxPrice
                        }
                        onChange={(e) => {
                          if (minPrice < e.target.value) {
                            setMaxPrice(e.target.value);
                          }
                        }}
                      />
                      <strong>{maxPrice} </strong>
                    </Col>
                  </Row>
                  <br />
                  <hr />
                  <div className="rateCheckBox">
                    <input
                      type="checkbox"
                      checked={searchByMaxPrice}
                      onChange={(e) => {
                        setByMaxPrice(e.target.checked);
                      }}
                    />
                    <label className="rateLabel">
                      &nbsp;Search&nbsp;by&nbsp;max&nbsp;price.
                    </label>
                  </div>
                  <div className="rateCheckBox">
                    <input
                      type="checkbox"
                      checked={searchByMaxRate}
                      onChange={(e) => {
                        setByMaxRate(e.target.checked);
                      }}
                    />
                    <label className="rateLabel">
                      &nbsp;Search&nbsp;by&nbsp;max&nbsp;rate.
                    </label>
                  </div>
                  <br />
                  <br />
                  <Row>
                    <Col xs={4}>
                      <Button variant="warning" onClick={onClearHandler}>
                        Clear
                      </Button>
                    </Col>
                    <Col xs={6}>
                      <Button variant="success" onClick={filterWines}>
                        Search
                      </Button>
                    </Col>
                  </Row>
                  &nbsp;
                </Form>
              </Row>
            </Container>

      </div>
      <div className="container py-4">
        <div className="col-md-10 col-lg-8 m-auto">
          <div className="bg-white rounded-3 shadow-sm p-4">
            <h4 className="mb-4">Leave a Question</h4>

            {/* <div className="alert alert-danger" role="alert"></div> */}

            <div className="d-flex">
              <img
                className="rounded-circle me-3"
                src="https://via.placeholder.com/128/{{this.randomColor}}/ffcbde.png?text={{this.StaringName}}"
              />
              <div className="flex-grow-1">
                <div className="hstack gap-2 mb-1">
                  <a href="#" className="fw-bold link-dark"></a>
                </div>
                <div className="form-floating mb-3">
                  <textarea
                    className="form-control w-100"
                    value={question}
                    onChange={(e) => {
                      setQuestion(e.target.value);
                    }}
                    required
                  ></textarea>
                  <label>Leave a message here</label>
                </div>

                <div className="hstack justify-content-end gap-2">
                  <button
                    className="btn btn-sm btn-link link-secondary text-uppercase"
                    title="Clear form"
                    onClick={() => {
                      setQuestion("");
                    }}
                  >
                    Clear
                  </button>
                  <button
                    disabled={question.trim() === ""}
                    className="btn btn-sm btn-primary text-uppercase"
                    onClick={onCreateQuestion}
                  >
                    comment
                  </button>
                </div>
              </div>
            </div>

            <br />
            <hr />
            <h4 className="mb-4">Questions</h4>

            <div></div>
            <br />
            <br />
            <hr />

            <div className="">
              <div className="py-3">
                {blogs.length !== 0 && (
                  <div className="table-wrapper">
                    {blogs[0].numberOfApplies.map((blog) => (
                      <div className="d-flex comment">
                       
                        <img
                          className="rounded-circle me-3"

                          src={`https://via.placeholder.com/130/${Math.floor(
                            Math.random() * 16777215
                          ).toString(16)}/ffcbde.png?text=${
                            blog.blogs.UserName.split(" ")[0][0] +
                            blog.blogs.UserName.split(" ")[1][0]
                          }`}
                          
                        />
                        <div className="flex-grow-1 ms-3">
                          <div className="mb-1">
                            <OverlayTrigger
                              trigger={user.length === 0 ? "click" : "focus"}
                              placement="right"
                              overlay={popover}
                            >
                              <Button
                                onClick={() => {
                                  getUser(blog.blogs.UserEmail);
                                }}
                                className="popoverButton"
                              >
                                { blog.blogs.UserName}
                              </Button>
                            </OverlayTrigger>
                            <FontAwesomeIcon
                              className="eyeFaIcon"
                              title={`Reply to ${ blog.blogs.UserName}`}
                              onClick={() => {
                                window.open(`/blogInfo/${ blog.blogs._id}`);
                              }}
                              icon={faReply}
                              size="lg"
                            />{" "}
                            <br />{" "}
                            <span className="text-muted text-nowrap">
                              {dateFormater(new Date( blog.blogs.QuestionDate))}
                            </span>
                          </div>

                          <div className="mb-2 css">{ blog.blogs.Question}</div>
                          <div className="hstack align-items-center mb-2">
                            <a className="link-primary me-2" href="#">
                              <i className="zmdi zmdi-thumb-up"></i>
                            </a>
                            <span className="me-3 small">
                              <strong>{ blog.numberOfApplies}</strong> aplies.
                            </span>
                            <a className="link-secondary me-4" href="#">
                              <i className="zmdi zmdi-thumb-down"></i>
                            </a>

                            {blog.blogs.UserEmail === userEmail && (
                              <button
                                className="btn btn-danger"
                                title="Delete Question!"
                              >
                                Delete
                              </button>
                            )}

                            <div
                              className="modal fade"
                              id="exampleModal"
                              role="dialog"
                              aria-labelledby="exampleModalLabel"
                              aria-hidden="true"
                            >
                              <div className="modal-dialog" role="document">
                                <div className="modal-content">
                                  <div className="modal-header">
                                    <h5
                                      className="modal-title"
                                      id="exampleModalLabel"
                                    >
                                      Modal title
                                    </h5>
                                    <button
                                      type="button"
                                      className="close"
                                      data-dismiss="modal"
                                      aria-label="Close"
                                    >
                                      <span aria-hidden="true">&times;</span>
                                    </button>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
                          <a
                            className="fw-bold d-flex align-items-center"
                            href="#"
                          >
                            <i className="zmdi zmdi-chevron-down fs-4 me-3"></i>
                          </a>
                        </div>
                      </div>
                    ))}
                  </div>
                )}

                <br />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Blog;
