import React from "react";
import "./SpecificFood.css";
import { Chart } from "react-google-charts";
import { useState, useEffect } from "react";
import { Col, Row, Spinner } from "react-bootstrap";
const SpecificFood = () => {
  const [results, setResults] = useState([]);
  const [choice, setChoice] = useState("");

  const getSpecificFoodResults = () => {
    let choiceDetails = {
      choice: choice,
    };
    fetch("https://winelovers.herokuapp.com/SpecificFood", {
      method: "POST",
      body: JSON.stringify(choiceDetails),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        console.log(
          json.opinion.filter(function (e) {
            return e.Color == "red";
          }).length
        );
        setResults(json);
      });
  };

  useEffect(() => {
    //getSpecificFoodResults();
  }, []);

  return <div>SpecificFood</div>;
};

export default SpecificFood;
