import React from "react";
import "./BigCategories.css";
import { Chart } from "react-google-charts";
import { useState, useEffect } from "react";
import { Col, Row } from "react-bootstrap";

const BigCategories = ({ choice }) => {
  const [results, setResults] = useState([]);

  const getBigCategoriesResults = () => {
    let choiceDetails = {
      choice: choice,
    };
    fetch("http://localhost:3000/bigCaregories", {
      method: "POST",
      body: JSON.stringify(choiceDetails),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        console.log(
          json.opinion.filter(function (e) {
            return e.Color == "red";
          }).length
        );
        setResults(json);
      });
  };

  useEffect(() => {
    getBigCategoriesResults();
  }, []);

  const dataType = [
    ["Type", "Type per Wine"],
    [
      "Dry",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Type == "Ξηρό";
          }).length
        : 0,
    ],
    [
      "Semi Dry",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Type == "semi dry";
          }).length
        : 0,
    ],
    [
      "Semi Sweet",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Type == "semi sweet";
          }).length
        : 0,
    ],
    [
      "Sweet",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Type == "sweet";
          }).length
        : 0,
    ],
  ];

  const optionsType = {
    title: `Wine & Type`,
  };

  // ------------ COLOR -----------------------
  const dataColor = [
    ["Type", "Type per Wine"],
    [
      "Red",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Color == "red";
          }).length
        : 0,
    ],
    [
      "Write",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Color == "white";
          }).length
        : 0,
    ],
    [
      "Roze",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Color == "roze";
          }).length
        : 0,
    ],
  ];
  const optionsColor = {
    title: `Wine & Color`,
  };

  // ------------ COLOR -----------------------
  const dataGrape = [
    ["Type", "Type per Wine"],
    [
      "Cabernet",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Grape == "red";
          }).length
        : 0,
    ],
    [
      "Agiorgitiko",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Grape == "white";
          }).length
        : 0,
    ],
    [
      "As",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Grape == "roze";
          }).length
        : 0,
    ],
  ];
  const optionsGrape = {
    title: `Wine  & Grape`,
  };

  return (
    <div>
      <div>
        {results.length !== 0 && (
          <Row>
            <Col xs={4}>
              <Chart
                chartType="PieChart"
                data={dataType}
                options={optionsType}
                width={"100%"}
                height={"300px"}
              />
            </Col>
            <Col xs={4}>
              <Chart
                chartType="PieChart"
                data={dataColor}
                options={optionsColor}
                width={"100%"}
                height={"300px"}
              />
            </Col>
            <Col xs={4}>
              <Chart
                chartType="PieChart"
                data={dataType}
                options={optionsGrape}
                width={"100%"}
                height={"300px"}
              />
            </Col>
          </Row>
        )}
      </div>
    </div>
  );
};

export default BigCategories;
