import React from 'react'
import './NavBar.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faBars} from '@fortawesome/free-solid-svg-icons'
import { Link } from 'react-router-dom'
import { useState } from 'react'


const NavBar = () => {

  

  // --------------------NavBar Active-----------------
  let navArray = [] ;
  for (let i = 0; i < 4; i++) {
    navArray[i]='' ;
  }
  navArray[0] = 'active' ;

  let [activeNavBar, setActiveNavBar] = useState(navArray);

  // --------------------------- Activation Function for Nav Links -------------------------------
  const activeSeminarHandler = (num) =>{
    console.log(num)
    for (let i = 0; i < 4; i++) {
      navArray[i]='' ;
    }
    navArray[num] = 'active' ;
    console.log(navArray)
    setActiveNavBar(navArray);
  }







    function myFunction() {
        var x = document.getElementById("myTopnav");
        if (x.className === "topnav") {
          x.className += " responsive";
        } else {
          x.className = "topnav";
        }
      }

    return (
        
            <div>
                <div className="topnav" id="myTopnav">
                    
                    <Link className={activeNavBar[0]} onClick={()=> activeSeminarHandler(0)} to="/" >Seminars</Link>
                    <Link className={activeNavBar[1]} onClick={()=> activeSeminarHandler(1)} to="/users">Users</Link>
                    <Link className={activeNavBar[2]}  onClick={()=> activeSeminarHandler(2)} to="/teams">Teams</Link>
                    <Link className={activeNavBar[3]}  onClick={()=> activeSeminarHandler(3)} to="/seminartype">Type</Link>
                    <a  className="icon" onClick={myFunction} >
                    <FontAwesomeIcon icon={faBars} />
                    </a>
                </div>


            </div>
        
    )
}

export default NavBar
