import { useEffect, useState } from "react";
import popupStyles from './popupdetails.module.css';
import PropTypes from "prop-types";


const PopUpUserDetails = (props) => {
    // --------------------DEFINE HOOKS ---------------------------
    const [show, setShow] = useState(false);
    const [ Range , setRange] = useState([]);
    
    const handleChange = (event) => {
        setRange(props)
    }


    const closeHandler = (e) => {
        setShow(false);
        props.onClose(false);
    };

    useEffect(() => {
        setShow(props.show);
    }, [props.show]);

    return (
        <div 
        style={{ visibility: show ? "visible" : "hidden", opacity: show ? "1" : "0"}}
        className={popupStyles.overlay}>
         <div className={popupStyles.popup}>
             <h4>{props.title}</h4>
             <span className={popupStyles.close} onClick={closeHandler}>&times;</span>

            <form className={popupStyles.formad}>
             <label className={popupStyles.labelad}>Seminar Name: </label><br/>
            <input  className = {popupStyles.inputad} type="text" /><br/>

             <label className={popupStyles.labelad}>Duration:<br/></label><br/>
             <input className = {popupStyles.inputad} type="text" /><br/>

             <label className={popupStyles.labelad}>Starting Date:<br/></label><br/>
             <input className = {popupStyles.inputad} onChange={handleChange} value={props.range[0]} type="text" /><br/>
            
            {props.range.length>1 && (
            <div>
             <label className={popupStyles.labelad}>Finish Date:<br/></label><br/>
             <input className = {popupStyles.inputad} onChange={handleChange}  value={props.range[1]}  type="text" /><br/>
             </div>
             ) }
            <button className={popupStyles.buttonad} type='submit' >Add Seminar</button>

            </form>

             <div className={popupStyles.content}>{props.children}</div>
         </div>
        </div>
    );

 }; 

 PopUpUserDetails.propTypes = {
    title: PropTypes.string.isRequired,
    show: PropTypes.bool.isRequired,
    onClose: PropTypes.func.isRequired
  };
  

  export default PopUpUserDetails;
