import React from "react";
import "./EditProfile.css";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
// import { Container } from "react-bootstrap";
import { Container, Row, Col } from "react-bootstrap";

const EditProfile = () => {
  const [user, setUser] = useState([]);
  const [newFirstName, setNewFirstName] = useState("");
  const [newLastName, setNewLastName] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newMobile, setNewMobile] = useState("");

  const cookies = new Cookies();
  var userEmail = cookies.get("email");

  const getUser = (email) => {
    var emailForSearch = email === undefined ? userEmail : email;
    fetch(`http://localhost:4000/getUser/${emailForSearch}`)
      .then((res) => res.json())
      .then((json) => {
        setUser([json]);
        console.log([json]);
      });
  };

  useEffect(() => {
    getUser();
  }, []);
  return (
    <div>
      {user.length !== 0 && (
        <div className="mainDiv">
          <div className="cardStyle">
            <h2 className="formTitle">Edit your Profile</h2>

            <Container>
              <Row>
                <Col xs={6}>
                  <div className="inputDiv">
                    <label className="inputLabel">First Name</label>
                    <input
                      value={newFirstName}
                      type="text"
                      id="editText"
                      placeholder={user[0].user.Name.split(" ")[0]}
                      onChange={(e) => {
                        setNewFirstName(e.target.value);
                      }}
                    />
                  </div>
                </Col>{" "}
                <Col xs={6}>
              <div className="buttonWrapper">
                <button
                  type="submit"
                  id="submitButton"
                  //onClick={onChangeHandler}
                  className="submitButton pure-button pure-button-primary"
                >
                  <span>Edit First Name</span>
                </button>
              </div>
            </Col>
              </Row>{" "}
              <Row>
                <Col xs={6}>
                  {" "}
                  <div className="inputDiv">
                    <label className="inputLabel">Last Name</label>
                    <input
                      value={newLastName}
                      type="text"
                      id="editText"
                      placeholder={user[0].user.Name.split(" ")[1]}
                      onChange={(e) => {
                        setNewLastName(e.target.value);
                      }}
                    />
                  </div>
                </Col>
                <Col xs={6}>
              <div className="buttonWrapper">
                <button
                  type="submit"
                  id="submitButton"
                  //onClick={onChangeHandler}
                  className="submitButton pure-button pure-button-primary"
                >
                  <span>Edit Last Name</span>
                </button>
              </div>
            </Col>
              </Row>
              <Row>
                <Col xs={6}>
                  <div className="inputDiv">
                    <label className="inputLabel">Email</label>
                    <input
                      value={newEmail}
                      type="email"
                      id="editText"
                      placeholder={user[0].user.Email}
                      onChange={(e) => {
                        setNewEmail(e.target.value);
                      }}
                    />
                  </div>
                </Col>
                <Col xs={6}>
              <div className="buttonWrapper">
                <button
                  type="submit"
                  id="submitButton"
                  //onClick={onChangeHandler}
                  className="submitButton pure-button pure-button-primary"
                >
                  <span>Continue to Edit</span>
                </button>
              </div>
            </Col>
              </Row>{" "}
              <Row>
                <Col xs={6}>
                  {" "}
                  <div className="inputDiv">
                    <label className="inputLabel">Mobile</label>
                    <input
                      value={newMobile}
                      type="text"
                      id="editText"
                      placeholder={user[0].user.Mobile}
                      onChange={(e) => {
                        setNewMobile(e.target.value);
                      }}
                    />
                  </div>
                </Col>
                <Col xs={6}>
              <div className="buttonWrapper">
                <button
                  type="submit"
                  id="submitButton"
                  //onClick={onChangeHandler}
                  className="submitButton pure-button pure-button-primary"
                >
                  <span>Continue to Edit</span>
                </button>
              </div>
            </Col>
              </Row>
            </Container>
            

            <br />
            <br />
            <br />
            <br />
            <br />
          </div>
        </div>
      )}
    </div>
  );
};

export default EditProfile;
