import React from "react";
import "./BigCategories.css";
import { Chart } from "react-google-charts";
import { useState, useEffect } from "react";
import { Col, Row, Spinner } from "react-bootstrap";

const BigCategories = ({ choice }) => {
  // ----------- width size -----------------------
  const [deviceSize, changeDeviceSize] = useState(window.innerWidth);
  useEffect(() => {
    const resizeW = () => changeDeviceSize(window.innerWidth);

    window.addEventListener("resize", resizeW); // Update the width on resize
    return () => window.removeEventListener("resize", resizeW);
  });
  const [results, setResults] = useState([]);

  const getBigCategoriesResults = () => {
    let choiceDetails = {
      choice: choice,
    };
    fetch("https://winelovers.herokuapp.com/bigCaregories", {
      method: "POST",
      body: JSON.stringify(choiceDetails),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        console.log(
          json.opinion.filter(function (e) {
            return e.Color == "red";
          }).length
        );
        setResults(json);
      });
  };

  useEffect(() => {
    getBigCategoriesResults();
  }, []);

  const dataType = [
    ["Type", "Type per Wine"],
    [
      "Dry",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Type == "Ξηρό";
          }).length
        : 0,
    ],
    [
      "Semi Dry",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Type == "semi dry";
          }).length
        : 0,
    ],
    [
      "Semi Sweet",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Type == "semi sweet";
          }).length
        : 0,
    ],
    [
      "Sweet",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Type == "sweet";
          }).length
        : 0,
    ],
  ];

  const optionsType = {
    title: `Wine & Type`,
    colors: ["#1f77b4", "#ff7f6e", "#d62728", "#2ca02c"],
  };

  // ------------ COLOR -----------------------
  const dataColor = [
    ["Type", "Type per Wine"],
    [
      "Red",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Color == "red";
          }).length
        : 0,
    ],
    [
      "Write",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Color == "white";
          }).length
        : 0,
    ],
    [
      "Roze",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Color == "roze";
          }).length
        : 0,
    ],
  ];
  const optionsColor = {
    title: `Wine & Color`,
    colors: ["#1f77b4", "#ff7f6e", "#d62728", "#2ca02c"],
  };

  // ------------ Grape -----------------------
  const dataGrape = [
    ["Type", "Type per Wine"],
    [
      "Cabernet",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Grape == "Cabernet";
          }).length
        : 0,
    ],
    [
      "Agiorgitiko",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Grape == "Agiorgitiko";
          }).length
        : 0,
    ],
    [
      "Asurtiko",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Grape == "Asurtiko";
          }).length
        : 0,
    ],
    [
      "Xinomauro",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Grape == "Xinomauro";
          }).length
        : 0,
    ],
  ];
  const optionsGrape = {
    title: `Wine  & Grape`,
    colors: ["#1f77b4", "#ff7f6e", "#d62728", "#2ca02c"],
  };

  // ------------ Year -----------------------
  const dataYear = [
    ["Type", "Type per Wine"],
    [
      "1950 or oldest",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Year == "1950 or oldest";
          }).length
        : 0,
    ],
    [
      "1950-1980",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Year == "1950-1980";
          }).length
        : 0,
    ],
    [
      "1980-2000",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Year == "1980-2000";
          }).length
        : 0,
    ],
    [
      "2000-2010",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Year == "2000-2010";
          }).length
        : 0,
    ],
    [
      "2010-2020",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Year == "2010-2020";
          }).length
        : 0,
    ],
    [
      "2020-present",
      results.length !== 0
        ? results.opinion.filter(function (e) {
            return e.Year == "2020-present";
          }).length
        : 0,
    ],
  ];
  const optionsYear = {
    title: `Wine  & Year`,
    colors: [...new Set(["#1f77b4", "#ff7f6e", "#d62728", "#2ca02c"])],
  };

  const options = {
    chart: {
      title: "Box Office Earnings in First Two Weeks of Opening",
      subtitle: "in millions of dollars (USD)",
    },
  };
  const data = [
    [
      "Year",
      "Guardians of the Galaxy",
      
    ],
    [0, 37.8, 80.8,],
    [2, 30.9, 69.5],
    [3, 25.4, 57,],
    [4, 11.7, 18.8],
    [5, 11.9, 17.6],
    [6, 8.8, 13.6],
    [7, 7.6, 12.3],
    [8, 12.3, 29.26],
    [9, 16.9, 42.9],
    [10, 12.8, 30.9],
    [11, 5.3, 7.9],
    [12, 6.6, 8.4, 5.2],
    [13, 4.8, 6.3, 3.6],
    [14, 4.2, 6.2, 3.4],
  ];

  return (
    <div>
      <div>
        {results.length === 0 && (
          <Spinner
            className="spiner-food-wine"
            animation="border"
            variant="primary"
          />
        )}
        {results.length !== 0 && (
          <>
            <Row>
              <Col xs={deviceSize < 1000 ? 12 : 4}>
                <Chart
                  chartType="PieChart"
                  data={dataType}
                  options={optionsType}
                  width={"100%"}
                  height={"300px"}
                />
              </Col>
              <Col xs={deviceSize < 1000 ? 12 : 4}>
                <Chart
                  chartType="PieChart"
                  data={dataColor}
                  options={optionsColor}
                  width={"100%"}
                  height={"300px"}
                />
              </Col>
              <Col xs={deviceSize < 1000 ? 12 : 4}>
                <Chart
                  chartType="PieChart"
                  data={dataGrape}
                  options={optionsGrape}
                  width={"100%"}
                  height={"300px"}
                />
              </Col>
            </Row>

            <Row>
              <Chart
                chartType="Line"
                width="100%"
                height="400px"
                data={data}
                options={options}
              />
            </Row>

            {/* <Row>
              
              <Col xs={6}>
                <Chart
                  chartType="PieChart"
                  data={dataYear}
                  options={optionsYear}
                  width={"100%"}
                  height={"300px"}
                />
              </Col>
              <Col xs={3}></Col>
            </Row> */}
          </>
        )}
      </div>
    </div>
  );
};

export default BigCategories;
