import React from "react";
import "./BigCategories.css";
import { Col, Row, Form, Button } from "react-bootstrap";
import { FaFish, FaPizzaSlice } from "react-icons/fa";
import { GiChickenOven } from "react-icons/gi";
import { TbMeat } from "react-icons/tb";
import { GiRopeCoil } from "react-icons/gi";
import { TbCheese } from "react-icons/tb";
import { useState, useEffect } from "react";

const BigCategories = ({ colorWines, typeWines, grapeWines }) => {
  const [deviceSize, changeDeviceSize] = useState(window.innerWidth);
  useEffect(() => {
    const resizeW = () => changeDeviceSize(window.innerWidth);

    window.addEventListener("resize", resizeW); // Update the width on resize
    return () => window.removeEventListener("resize", resizeW);
  });

  const [redMeat, setReadMeat] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [whiteMeat, setWhiteMeat] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [fish, setFish] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [cheece, setCheece] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [pasta, setPasta] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [pizza, setPizza] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  return (
    <div>
      <div>
        <h5>&#8226; Big Categories</h5>
        <div className="helpUsTitleContent">
          <p>- Wine would you pair with : </p>

          <div className="helpUsTitleContent">
            <p>
              <strong>1.</strong> Red Meat <TbMeat className="reactIcon" />
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: e.target.value,
                      Color: redMeat.Color,
                      Grape: redMeat.Grape,
                      Year: redMeat.Year,
                    });
                  }}
                >
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Color</Form.Label>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: redMeat.Type,
                      Color: e.target.value,
                      Grape: redMeat.Grape,
                      Year: redMeat.Year,
                    });
                  }}
                >
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Grape</Form.Label>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: redMeat.Type,
                      Color: redMeat.Color,
                      Grape: e.target.value,
                      Year: redMeat.Year,
                    });
                  }}
                >
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Year&nbsp;Range</Form.Label>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: redMeat.Type,
                      Color: redMeat.Color,
                      Grape: redMeat.Grape,
                      Year: e.target.value,
                    });
                  }}
                >
                  <option>Small select</option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>2.</strong> White Meat &nbsp;{" "}
              <GiChickenOven className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Type</Form.Label>
                <Form.Select size="sm">
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Color</Form.Label>
                <Form.Select size="sm">
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Grape</Form.Label>
                <Form.Select size="sm">
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Year&nbsp;Range</Form.Label>
                <Form.Select size="sm">
                  <option>Small select</option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>3.</strong> Fish &nbsp; <FaFish className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Type</Form.Label>
                <Form.Select size="sm">
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Color</Form.Label>
                <Form.Select size="sm">
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Grape</Form.Label>
                <Form.Select size="sm">
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Year&nbsp;Range</Form.Label>
                <Form.Select size="sm">
                  <option>Small select</option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>4.</strong> Cheece &nbsp;{" "}
              <TbCheese className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Type</Form.Label>
                <Form.Select size="sm">
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Color</Form.Label>
                <Form.Select size="sm">
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Grape</Form.Label>
                <Form.Select size="sm">
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Year&nbsp;Range</Form.Label>
                <Form.Select size="sm">
                  <option>Small select</option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>5.</strong> Pasta <GiRopeCoil className="reactIcon" />
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Type</Form.Label>
                <Form.Select size="sm">
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Color</Form.Label>
                <Form.Select size="sm">
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Grape</Form.Label>
                <Form.Select size="sm">
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Year&nbsp;Range</Form.Label>
                <Form.Select size="sm">
                  <option>Small select</option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>6.</strong> Pizza &nbsp;{" "}
              <FaPizzaSlice className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Type</Form.Label>
                <Form.Select size="sm">
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Color</Form.Label>
                <Form.Select size="sm">
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Grape</Form.Label>
                <Form.Select size="sm">
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Year&nbsp;Range</Form.Label>
                <Form.Select size="sm">
                  <option>Small select</option>
                </Form.Select>
              </Col>
            </Row>
          </div>
        </div>
        <br />
        <Row>
          <Col xs={1}>
            <Button
              variant="danger"
              onChange={() => {
                setReadMeat({
                  Type: "",
                  Color: "",
                  Grape: "",
                  Year: "",
                });
                setWhiteMeat({
                  Type: "",
                  Color: "",
                  Grape: "",
                  Year: "",
                });
                setFish({
                  Type: "",
                  Color: "",
                  Grape: "",
                  Year: "",
                });
                setCheece({
                  Type: "",
                  Color: "",
                  Grape: "",
                  Year: "",
                });
                setPasta({
                  Type: "",
                  Color: "",
                  Grape: "",
                  Year: "",
                });
                setPizza({
                  Type: "",
                  Color: "",
                  Grape: "",
                  Year: "",
                });
              }}
            >
              Clear
            </Button>
          </Col>
          <Col xs={1}></Col>
          <Col xs={1}>
            <Button
              variant="success"
              onClick={() => {
                console.log(redMeat);
              }}
            >
              Aply
            </Button>
          </Col>
        </Row>

        <hr />
      </div>
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
    </div>
  );
};

export default BigCategories;
