import React from 'react'
import { useParams } from 'react-router'
import './SeminarDetails.css'
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import ToolkitProvider, { Search } from 'react-bootstrap-table2-toolkit';
import cellEditFactory from 'react-bootstrap-table2-editor';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.css';
import 'react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css';
import Spiner from '../Components/Spiner';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faTrashAlt} from '@fortawesome/free-solid-svg-icons'
import '../Components/Spiner.css'

const SeminarDetails = () => {

    const seminarCategories = [
        {id: 1 , seminar: "seminar1-part1", duration: 30, starting_date: "1/7/2021" , organization: "Agile Actors", type: 'ITNextGen Fundamentals' },
        {id: 2 , seminar: "seminar1-part2", duration: 15, starting_date: "1/7/2021" , organization: "Agile Actors", type: 'ITNextGen Fundamentals'},
        {id: 3 , seminar: "seminar1-part3", duration: 20, starting_date: "15/5/2020", organization: "Agile Actors", type: 'ITNextGen Fundamentals' },
        {id: 4 , seminar: "seminar1-part4", duration: 7, starting_date: "15/5/2020", organization: "Agile Actors", type: 'ITNextGen Fundamentals' },
        {id: 5 , seminar: "seminar1-part5", duration: 20, starting_date: "15/5/2020", organization: "Agile Actors", type: 'ITNextGen Fundamentals'},
        {id: 6 , seminar: "seminar1-part6", duration: 30, starting_date: "3/10/2019" , organization: "Agile Actors", type: 'ITNextGen Fundamentals'},
        {id: 7 , seminar: "seminar1-part7", duration: 30, starting_date: "1/7/2021" , organization: "Agile Actors", type: 'ITNextGen Fundamentals'},
        {id: 8 , seminar: "seminar1-part8", duration: 30, starting_date: "15/5/2020 ", organization: "Agile Actors", type: 'ITNextGen Fundamentals'},
        {id: 9 , seminar: "seminar1-part9", duration: 30, starting_date: "1/7/2021" , organization: "Agile Actors", type: 'ITNextGen Fundamentals'},
        {id: 10 , seminar: "seminar1-part10", duration: 30, starting_date: "3/10/2019", organization: "Agile Actors", type: 'ITNextGen Fundamentals' },
        {id: 11 , seminar: "seminar1-part11", duration: 15, starting_date: "1/7/2021" , organization: "Agile Actors", type: 'ITNextGen Fundamentals'},
        {id: 12 , seminar: "seminar1-part12", duration: 20, starting_date: "15/5/2020" , organization: "Agile Actors", type: 'ITNextGen Fundamentals'},
        {id: 13 , seminar: "seminar1-part13", duration: 7, starting_date: "15/5/2020 ", organization: "Agile Actors", type: 'ITNextGen Fundamentals'},
        {id: 14 , seminar: "seminar1-part14", duration: 20, starting_date: "1/7/2021" , organization: "Agile Actors", type: 'ITNextGen Fundamentals'},
        {id: 15 , seminar: "seminar1-part15", duration: 30, starting_date: "15/5/2020", organization: "Agile Actors", type: 'ITNextGen Fundamentals' },
        {id: 16 , seminar: "seminar1-part16", duration: 30, starting_date: "15/5/2020", organization: "Agile Actors", type: 'ITNextGen Fundamentals' },
        {id: 17 , seminar: "seminar1-part17", duration: 30, starting_date: "1/7/2021", organization: "Agile Actors", type: 'ITNextGen Fundamentals' },
        {id: 18 , seminar: "seminar1-part18", duration: 30, starting_date: "15/5/2020" , organization: "Agile Actors", type: 'ITNextGen Fundamentals'},
        
      ];

    let { id } = useParams();

    const columns = [
        {dataField: 'id', text:'Id', sort: true, hidden:true},
        {dataField: 'seminar', text:'Seminar', sort: true},
        {dataField: 'duration', text:'Duration', sort: true, searchable:false },
        {dataField: 'starting_date', text:'Starting_date', sort: true,  },
        {dataField: 'organization', text:'Organization', sort: true,  },
        {dataField: 'type', text:'Type', sort: true,  },
        {   dataField: 'Actions', text:'Actions', sort: true, 
            formatter: (cellContent, row) => {
                
                return (
                <div>
                  <button key = {row.id}
                    className="btn btn-danger btn-xs"
                    //---------------- DELETE FUNCTION ------------------
                    onClick= {() => {
                        console.log("Delete Button Clicked")
                        console.log(row)
                    }}
                  >
                    Delete
                  </button>
                  &nbsp; &nbsp; 
                  <button key = {row.id+200}
                    className="btn btn-warning btn-xs"
                    //---------------- DELETE FUNCTION ------------------
                    onClick= {() => {
                        console.log("Edit Button Clicked")
                        console.log(row)
                    }}
                  >
                    Edit
                  </button>
                </div>  
                );
              }
        }
        

        
      ]

     
    
      const defaultSorted = [{
        dataField: 'id',
        order: 'asc'
      }];


      const pagination = paginationFactory({
        page: 1,
        sizePerPage: 5,
        lastPageText: '>>',
        firstPageText: '<<',
        nextPageText: '>',
        prePageText: '<',
        showTotal: true,
        alwaysShowAllBtns: true,
        onPageChange: function (page, sizePerPage) {
          console.log('page', page);
          console.log('sizePerPage', sizePerPage);
        },
        onSizePerPageChange: function (page, sizePerPage) {
          console.log('page', page);
          console.log('sizePerPage', sizePerPage);
        }
      });


      const { SearchBar, ClearSearchButton } = Search;

    return (
        <div>
            <div className="b">Details for Seminar {id}</div>
            <div className='BootsTrapTableSeminar'>
                <div className='SearchBar'>
                    <ToolkitProvider
                        bootstrap4
                        keyField='id'
                        data={seminarCategories}
                        columns={columns}
                        search

                    >
                        {
                        props => (
                            <div>
                            <h6>Search for Seminars:</h6>
                            <SearchBar {...props.searchProps} />
                            <ClearSearchButton {...props.searchProps} />
                            <hr />
                            <FontAwesomeIcon className = 'buttonplus'  size= '1x' icon={faTrashAlt} />
                            <BootstrapTable
                            
                                defaultSorted={defaultSorted}
                                pagination={pagination}
                                {...props.baseProps}
                                
                            />
                            </div>
                        )
                        }
                    </ToolkitProvider>

                 </div>
                   
                </div> 
            
        </div>
    )
}

export default SeminarDetails
