import React from "react";
import "./ChangePassword.css";
import { Button, Container, Row, Col, Form, Alert } from "react-bootstrap";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";

const ChangePassword = () => {
  const [newPassword, setNewPassword] = useState("");
  const [newConfirmPassword, setNewConfirmPassword] = useState("");
  const cookies = new Cookies();
  var userEmail = cookies.get("email");

  return (
    <div>
      <div className="mainDiv">
        <div className="cardStyle">
          <h2 className="formTitle">Login to your account</h2>

          <div className="inputDiv">
            <label className="inputLabel" for="password">
              New Password
            </label>
            <input
              value={newPassword}
              type="password"
              id="password"
              name="password"
              
              onChange={(e) => {
                setNewPassword(e.target.value);
              }}
            />
          </div>

          <div className="inputDiv">
            <label className="inputLabel" for="confirmPassword">
              Confirm Password
            </label>
            <input
              value={newConfirmPassword}
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              onChange={(e) => {
                setNewConfirmPassword(e.target.value);
              }}
            />
          </div>

          <div className="buttonWrapper">
            <button
              type="submit"
              id="submitButton"
              disabled = {  (newPassword !=="" && newPassword !== newConfirmPassword && newPassword.l) || newPassword ==="" }
              onclick="validateSignupForm()"
              className="submitButton pure-button pure-button-primary"
            >
              <span>Continue to Change</span>
            </button>
          </div>
          <br />
          <br />
          <br />
          <br />
          <br />
        </div>
      </div>
    </div>
  );
};

export default ChangePassword;
