import React from "react";
import { useParams } from "react-router-dom";
import "./BlogInfo.css";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
import { Button, Container, Row, Col, Form, Modal } from "react-bootstrap";
// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import { faCartPlus } from "@fortawesome/free-solid-svg-icons";
// import { faFilter } from "@fortawesome/free-solid-svg-icons";
const BlogInfo = () => {
  const [blogInfo, setBlogInfo] = useState([]);
  const { id } = useParams();
  const cookies = new Cookies();
  var userEmail = cookies.get("email");

  const getApliesToQuestion = () => {
    fetch(`http://localhost:4000/getApliesToQuestion/${id}`)
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setBlogInfo(json);
      });
  };
  useEffect(() => {
    getApliesToQuestion();
  }, []);

  return <div>

<div className="container py-4">
        <div className="col-md-10 col-lg-8 m-auto">
            
            <div className="bg-white rounded-3 shadow-sm p-4">

                <h4 className="mb-4"> 
                {/* {{number.number}} */}
                 Comments</h4>

              
                <div className="">
                    <div className="py-3">
                   
                    <div className="d-flex comment">
                        <img className="rounded-circle me-3"
                       
                            src="https://via.placeholder.com/128/{{this.randomColor}}/ffcbde.png?text={{this.startingName}}" />
                        <div className="flex-grow-1 ms-3">
                            
                            <div className="mb-1"><a href="#" className="fw-bold link-dark me-1">
                              {/* {{this.UserName}} */}
                              </a> <span className="text-muted text-nowrap">2 days ago</span></div>
                            <div className="mb-2">
                              {/* {{this.Question}} */}
                              </div>
                            <div className="hstack align-items-center mb-2">
                                <a className="link-primary me-2" href="#"><i className="zmdi zmdi-thumb-up"></i></a>
                                <span className="me-3 small">55</span>
                                <a className="link-secondary me-4" href="#"><i className="zmdi zmdi-thumb-down"></i></a>
                                <a className="link-secondary small" href="#">REPLY</a>
                                <a className="link-danger small ms-3" href="#">DELETE</a>
                            </div>
                            <a className="fw-bold d-flex align-items-center" href="#">
                                <i className="zmdi zmdi-chevron-down fs-4 me-3"></i>
                                <span>Hide Replies</span>
                            </a>
                        </div>
                    </div>
                   

                    
                    <div className="bg-white rounded-3 shadow-sm p-4 mb-4">
                       
                        <div className="d-flex">
                            <img className="rounded-circle me-3"
                                
                                src="https://via.placeholder.com/128/{{this.randomColor}}/ffcbde.png?text={{this.startingName}}" />
                            <div className="flex-grow-1">
                            <div className="hstack gap-2 mb-1">
                                <a href="#" className="fw-bold link-dark">
                                  
                                  </a>
                            </div>

                            <form action="/auth/aplyToQuestion" method="POST">
                                <div className="form-floating mb-3">
                                    <textarea className="form-control w-100"
                                                placeholder="Leave a comment here"
                                                name="aplieMessage"
                                                required></textarea>
                                    <label for="my-comment">Aplie here...</label>
                                    
                                    
                                </div>
                                <div className="hstack justify-content-end gap-2">
                                    
                                    <button type="submit" className="btn btn-sm btn-primary text-uppercase">comment</button>
                                </div>
                            </form>

                            </div>
                        </div>
                  


                        <div className="comment-replies mt-4 bg-light p-3 rounded">
                        
                        <h4 className="mb-4"> 
                        {/* {{number.number}}  */}
                        Aplies to 
                        <strong>
                          {/* {{number.name}} */}
                          </strong> </h4>
                 
                        
                        <div className="d-flex py-2">
                            <img className="rounded-circle me-3"
                        style="width:3rem;height:3rem;"
                                src="https://via.placeholder.com/128/{{this.randomColor}}/7f00ff.png?text={{this.StartingName}}" />
                            <div className="flex-grow-1 ms-3">
                                <div className="mb-1"><a href="/profilInfo/{{this.AplierId}}" className="fw-bold link-dark pe-1">
                                  {/* {{this.AplierName}} */}
                                  </a> <span className="text-muted text-nowrap">1 day ago</span></div>
                                <div className="mb-2">
                                  {/* {{this.Aply}} */}
                                  </div>
                                <div className="hstack align-items-center">
                                <a className="link-secondary me-2" href="#"><i className="zmdi zmdi-thumb-up"></i></a>
                                <span className="me-3 small">1</span>
                                <a className="link-secondary me-4" href="#"><i className="zmdi zmdi-thumb-down"></i></a>
                                <a className="link-secondary small" href="#">REPLY</a>
                                </div>
                            </div>
                        </div>
                        
                        
                    </div>
                    </div>
                    
                    


                    </div>

                    
                </div>

            </div>
        </div>
        </div>
  </div>;
};

export default BlogInfo;
