import React from "react";
import "./History.css";
import "./Card.css";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { dateFormater, addDays } from "./Functions/functions";
import { Table } from "react-bootstrap";

const History = () => {
  

  return (
    <div>
      <h3 className="HistoryOrderTable">History Order Table</h3>
      <Table striped bordered hover className="tableHistory">
        <thead>
          <tr>
            <th>Wine Names</th>
            <th>Bootle Price</th>
            <th>Amount</th>
            <th>Total Price</th>
            <th>Order Date</th>
            <th>Shipping Date</th>
            <th>Address</th>
            <th>Payment Kind</th>
            <th>Extra Details</th>
          </tr>
        </thead>
        {history.length !== 0 && (
          <tbody>
            {history.map((item) => (
              <tr>
                <td>
                  {item.wines.map((wine) => (
                    <strong>
                      <a href={`/wine/${wine._id}`}>{wine.WineName}</a>
                      <br />
                    </strong>
                  ))}
                </td>
                <td>
                  {item.wines.map((wine) => (
                    <strong>
                      {wine.Price} €
                      <br />
                    </strong>
                  ))}
                </td>
                <td>Otto</td>
                <td>
                  <strong>{item.history.Price} €</strong>
                </td>
                <td>
                  <strong>{dateFormater(new Date(item.history.Date))}</strong>
                </td>
                <td>
                  {dateFormater(
                    new Date(
                      new Date(item.history.Date).setDate(
                        new Date(item.history.Date).getDate() + 3
                      )
                    )
                  )}
                </td>
                <td>Otto</td>
                <td>@mdo</td>
              </tr>
            ))}
          </tbody>
        )}
      </Table>
    </div>
  );
};

export default History;
