import React from "react";
import "./BigCategories.css";
import { Col, Row, Form, Button } from "react-bootstrap";
import { FaFish, FaPizzaSlice } from "react-icons/fa";
import { GiChickenOven } from "react-icons/gi";
import { TbMeat } from "react-icons/tb";
import { GiRopeCoil } from "react-icons/gi";
import { TbCheese } from "react-icons/tb";
import { useState, useEffect } from "react";

const BigCategories = ({ colorWines, typeWines, grapeWines }) => {
  const [deviceSize, changeDeviceSize] = useState(window.innerWidth);
  useEffect(() => {
    const resizeW = () => changeDeviceSize(window.innerWidth);

    window.addEventListener("resize", resizeW); // Update the width on resize
    return () => window.removeEventListener("resize", resizeW);
  });

  const [redMeat, setReadMeat] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [whiteMeat, setWhiteMeat] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [fish, setFish] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [cheece, setCheece] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [pasta, setPasta] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [pizza, setPizza] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  return (
    <div>
      <div>
        <h5>&#8226; Big Categories</h5>
        <div className="helpUsTitleContent">
          <p>- Wine would you pair with : </p>

          <div className="helpUsTitleContent">
            <p>
              <strong>1.</strong> Red Meat <TbMeat className="reactIcon" />
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: e.target.value,
                      Color: redMeat.Color,
                      Grape: redMeat.Grape,
                      Year: redMeat.Year,
                    });
                  }}
                >
                  <option value="">Select Type</option>
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: redMeat.Type,
                      Color: e.target.value,
                      Grape: redMeat.Grape,
                      Year: redMeat.Year,
                    });
                  }}
                >
                  <option value="">Select color</option>
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: redMeat.Type,
                      Color: redMeat.Color,
                      Grape: e.target.value,
                      Year: redMeat.Year,
                    });
                  }}
                >
                  <option value="">Select grape</option>
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: redMeat.Type,
                      Color: redMeat.Color,
                      Grape: redMeat.Grape,
                      Year: e.target.value,
                    });
                  }}
                >
                  <option value="">Select year</option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>2.</strong> White Meat &nbsp;{" "}
              <GiChickenOven className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setWhiteMeat({
                      Type: e.target.value,
                      Color: whiteMeat.Color,
                      Grape: whiteMeat.Grape,
                      Year: whiteMeat.Year,
                    });
                  }}
                >
                  <option value="">Select Type</option>
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setWhiteMeat({
                      Type: whiteMeat.Type,
                      Color: e.target.value,
                      Grape: whiteMeat.Grape,
                      Year: whiteMeat.Year,
                    });
                  }}
                >
                  <option value="">Select color</option>
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setWhiteMeat({
                      Type: whiteMeat.Type,
                      Color: whiteMeat.Color,
                      Grape: e.target.value,
                      Year: whiteMeat.Year,
                    });
                  }}
                >
                  <option value="">Select grape</option>
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setWhiteMeat({
                      Type: whiteMeat.Type,
                      Color: whiteMeat.Color,
                      Grape: whiteMeat.Grape,
                      Year: e.target.value,
                    });
                  }}
                >
                  <option value="">Select year</option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>3.</strong> Fish &nbsp; <FaFish className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setFish({
                      Type: e.target.value,
                      Color: fish.Color,
                      Grape: fish.Grape,
                      Year: fish.Year,
                    });
                  }}
                >
                  <option value="">Select Type</option>
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setFish({
                      Type: fish.Type,
                      Color: e.target.value,
                      Grape: fish.Grape,
                      Year: fish.Year,
                    });
                  }}
                >
                  <option value="">Select color</option>
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setFish({
                      Type: fish.Type,
                      Color: fish.Color,
                      Grape: e.target.value,
                      Year: fish.Year,
                    });
                  }}
                >
                  <option value="">Select grape</option>
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setFish({
                      Type: fish.Type,
                      Color: fish.Color,
                      Grape: fish.Grape,
                      Year: e.target.value,
                    });
                  }}
                >
                  <option value="">Select year</option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>4.</strong> Cheece &nbsp;{" "}
              <TbCheese className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setCheece({
                      Type: e.target.value,
                      Color: cheece.Color,
                      Grape: cheece.Grape,
                      Year: cheece.Year,
                    });
                  }}
                >
                  <option value="">Select Type</option>
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setCheece({
                      Type: cheece.Type,
                      Color: e.target.value,
                      Grape: cheece.Grape,
                      Year: cheece.Year,
                    });
                  }}
                >
                  <option value="">Select color</option>
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setCheece({
                      Type: cheece.Type,
                      Color: cheece.Color,
                      Grape: e.target.value,
                      Year: cheece.Year,
                    });
                  }}
                >
                  <option value="">Select grape</option>
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setCheece({
                      Type: cheece.Type,
                      Color: cheece.Color,
                      Grape: cheece.Grape,
                      Year: e.target.value,
                    });
                  }}
                >
                  <option value="">Select year</option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>5.</strong> Pasta <GiRopeCoil className="reactIcon" />
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setPasta({
                      Type: e.target.value,
                      Color: pasta.Color,
                      Grape: pasta.Grape,
                      Year: pasta.Year,
                    });
                  }}
                >
                  <option value="">Select Type</option>
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setPasta({
                      Type: pasta.Type,
                      Color: e.target.value,
                      Grape: pasta.Grape,
                      Year: pasta.Year,
                    });
                  }}
                >
                  <option value="">Select color</option>
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setPasta({
                      Type: pasta.Type,
                      Color: pasta.Color,
                      Grape: e.target.value,
                      Year: pasta.Year,
                    });
                  }}
                >
                  <option value="">Select grape</option>
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setPasta({
                      Type: pasta.Type,
                      Color: pasta.Color,
                      Grape: pasta.Grape,
                      Year: e.target.value,
                    });
                  }}
