import React from "react";
import BootstrapTable from "react-bootstrap-table-next";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
import { Button, Container, Row, Col, Form, Modal } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCartPlus } from "@fortawesome/free-solid-svg-icons";
import "./Wines.css";

const Wines = () => {
  const [wines, setWines] = useState([]);
  const [selectedWineId, setSelectedWineId] = useState("");

  // -------- MODAL
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  // --------- Set COOKIES
  const cookies = new Cookies();
  var user = cookies.get("email");

  //------ initial Wines --------------
  const getWines = () => {
    fetch("http://localhost:4000/getWines")
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setWines(json);
      });
  };

  useEffect(() => {
    getWines();
  }, []);

  // --------- Add To Card ----------------

  const onAddToCardHandler = (id) => {
    let cardDetails = {
      wineId: id,
      email: user,
    };
    fetch("http://localhost:4000/addToCard", {
      method: "POST",
      body: JSON.stringify(cardDetails),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
      });
  };

  return (
    <div>
      <>
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title className="modalTitle">
              Add <strong>{selectedWineId.split(",")[1]}</strong> to Card
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form.Label>Chooce Amount</Form.Label>
            <Form.Control
              type="number"
              min="1"
              max="20"
              placeholder="Chooce Amount"
            />
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={() => {handleClose(selectedWineId.split(",")[1])} }>
              Add to card
            </Button>
          </Modal.Footer>
        </Modal>

      </>
      <div className="twoDivs">
        <div className="filters">
          <h4>&nbsp; Search any wine.</h4>

          <Container>
            <Row>
              <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Email address</Form.Label>
                  <Form.Control type="email" placeholder="Enter email" />
                  <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                  </Form.Text>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control type="password" placeholder="Password" />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicCheckbox">
                  <Form.Check type="checkbox" label="Check me out" />
                </Form.Group>
                <Button variant="primary">Search</Button>
              </Form>
            </Row>
          </Container>
        </div>

        <div className="container">
          <Container>
            {wines.length !== 0 && (
              <Row>
                <Col xs={2}></Col>
                <Col xs={10}></Col>
                {wines.map((item) => (
                  <Col xs={4}>
                    {wines.length !== 0 && (
                      <Container className="item">
                        <div className="card card-body">
                          <div className="media align-items-center align-items-lg-start text-center text-lg-left flex-column flex-lg-row">
                            <div className="mr-2 mb-3 mb-lg-0">
                              <img className="image" src={item.ImageUrl} />
                            </div>
                            <div className="media-body">
                              <h6 className="media-title font-weight-semibold">
                                <a href={`/wine/${item._id}`} data-abc="true">
                                  {" "}
                                  {item.WineName}
                                </a>
                              </h6>
                              <ul className="list-inline list-inline-dotted mb-3 mb-lg-2">
                                <li className="list-inline-item">
                                  <a
                                    href="#"
                                    className="text-muted"
                                    data-abc="true"
                                  >
                                    {item.Type}
                                  </a>
                                </li>
                                <li className="list-inline-item">
                                  <a
                                    href="#"
                                    className="text-muted"
                                    data-abc="true"
                                  >
                                    {item.Color}
                                  </a>
                                </li>
                                <li className="list-inline-item">
                                  <a
                                    href="#"
                                    className="text-muted"
                                    data-abc="true"
                                  >
                                    {item.Year}
                                  </a>
                                </li>
                                <br />
                                <li className="list-inline-item">
                                  <a
                                    href="#"
                                    className="text-muted"
                                    data-abc="true"
                                  >
                                    {item.Location} , {item.Country}
                                  </a>
                                </li>
                              </ul>
                              <p className="mb-3">{item.WineDescription}</p>
                              <ul className="list-inline list-inline-dotted mb-0">
                                <li className="list-inline-item">
                                  {" "}
                                  <strong>Winery by</strong>{" "}
                                  <a href="#" data-abc="true">
                                    {" "}
                                    {item.Winery}{" "}
                                  </a>
                                </li>
                                <br />
                                <li className="list-inline-item">
                                  {" "}
                                  <strong>Grape : </strong> {item.Grapes}{" "}
                                </li>
                              </ul>
                            </div>
                            <div className="mt-3 mt-lg-0 ml-lg-3 text-center">
                              <h3 className="mb-0 font-weight-semibold"> €</h3>
                              <div className="text-muted">
                                {" "}
                                <span className="fa fa-star checked"></span>{" "}
                              </div>
                              <div className="text-muted"> 0 reviews </div>
                              <div className="text-muted"> </div>

                              <Button
                                variant="warning"
                                title="Add to Card"
                                onClick={() => {
                                  setSelectedWineId(
                                    item._id + "," + item.WineName
                                  );
                                  handleShow(item._id);
                                }}
                              >
                                <FontAwesomeIcon icon={faCartPlus} size='1x' />
                              </Button>
                            </div>
                          </div>
                        </div>
                      </Container>
                    )}
                  </Col>
                ))}
              </Row>
            )}
          </Container>
        </div>
      </div>
    </div>
  );
};

