import React from "react";
import "./Card.css";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { Alert } from "react-bootstrap";
import { Col, Row } from "react-bootstrap";

const Card = () => {
  const [totalPrice, setTotalPrice] = useState(0);
  const [kindPayment, setKindPayment] = useState("");
  const [address, setAddress] = useState("");
  const [phoneNum, setPhoneNum] = useState("");
  const [zipCode, setZipCode] = useState("");
  const [userDetails, setUseretails] = useState([]);
  const [cardNumber, setCardNumber] = useState("");
  const [cardDate, setCardDate] = useState("");
  const [cardCVV, setCardCVV] = useState("");

  const cookies = new Cookies();
  var userEmail = cookies.get("email");
  console.log(cookies.get("card"));

  // ------------ Get user Details -----------------
  const getUser = () => {
    fetch(`http://localhost:4000/getUser/${userEmail}`)
      .then((res) => res.json())
      .then((json) => {
        console.log(json.user);
        setAddress(json.user.Address);
        setPhoneNum(json.user.Mobile);
        setUseretails([json]);
      });
  };
  useEffect(() => {
    getUser();
  }, []);

  var cardDetails =
    cookies.get("card") === "" || cookies.get("card") === undefined
      ? []
      : cookies.get("card").split("|");

  const [card, setCard] = useState(cardDetails);

  // -------- Delete from Card ----------
  const onDeletefromCard = (item) => {
    var cardCookies = cookies.get("card");
    var cardArray = cardCookies.split("|");
    cardArray = cardArray.filter((word) => word !== item);

    if (cardArray === [""]) {
      cookies.remove("card");
      // cookies.set("card", undefined , { path: "/" });
      setCard([]);
    } else {
      cookies.set("card", cardArray.join("|"), { path: "/" });
      setCard(cardArray);
    }
  };

  useEffect(() => {
    if (card.length === 0) {
      setTotalPrice(0);
    } else {
      var total_price = 0;
      for (const key in card) {
        total_price =
          Number(total_price) +
          Number(card[key].split(",")[5] * card[key].split(",")[7]);
      }
      setTotalPrice(total_price);
    }
  }, [card]);

  const onCreateOrder = () => {
    var wineList = [];
    for (const key in card) {
      wineList.push(card[key].split(",")[0]);
    }
    // console.log(totalPrice);
    // console.log(userEmail);
    // console.log(wineList)

    let cardDetails = {
      wineList: wineList,
      email: userEmail,
      totalPrice: totalPrice,
    };
    fetch("http://localhost:4000/completeOrder", {
      method: "POST",
      body: JSON.stringify(cardDetails),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        cookies.remove("card");

        setCard([]);
      });
  };

  return (
    <div>
      <Row>
        <Col xs={9}>
          <div className="product-details mr-2">
            <div className="d-flex flex-row align-items-center">
              <i className="fa fa-long-arrow-left"></i>
              <button className="ml-2">Continue Shopping</button>
            </div>
            <hr />
            <h6 className="mb-0">Shopping cart</h6>

            {card.length === 0 && (
              <Alert className="emptyAlert" variant="warning">
                The Card is Empty!
              </Alert>
            )}
            {card.length !== 0 && (
              <div>
                <div className="d-flex justify-content-between">
                  <span>
                    You have <strong>{card.length} items</strong> in your cart
                  </span>
                  <strong>Total Price : {totalPrice} €</strong>
                </div>

                {card.map((item) => (
                  <div className="d-flex justify-content-between align-items-center mt-3 p-2 items rounded">
                    <div className="d-flex flex-row">
                      <a href={`/wine/${item.split(",")[0]}`}>
                        <img
                          className="cardImage"
                          src={item.split(",")[6]}
                          width="40"
                        />
                      </a>
                      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                      <div className="ml-2">
                        <span className="font-weight-bold d-block">
                          {item.split(",")[1]}
                        </span>
                        <span className="spec">
                          {item.split(",")[4]} , {item.split(",")[3]}
                        </span>
                      </div>
                    </div>
                    <div className="d-flex flex-row align-items-center">
                      <span className="d-block">
                        {/* {formatDate(new Date(item.card.CardDate))}{" "} */}
                        &nbsp;&nbsp;&nbsp;
                      </span>{" "}
                      &nbsp;{" "}
                      <span className="d-block">{item.split(",")[7]}</span>
                      <strong>&nbsp; x &nbsp;</strong>
                      <span className="d-block ml-5 font-weight-bold">
                        {item.split(",")[5]}
                        &nbsp; = &nbsp;
                      </span>
                      <span className="d-block ml-5 font-weight-bold">
                        <strong>
                          {item.split(",")[7] * item.split(",")[5]} &nbsp;€
                        </strong>
                        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                        <FontAwesomeIcon
                          className="deleteIcon"
                          onClick={() => {
                            onDeletefromCard(item);
                          }}
                          icon={faTrash}
                          size="1x"
                        />
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </Col>
        <Col xs={3}></Col>
      </Row>
      <div className="cardboth">
        
      </div>
    </div>
  );
};

export default Card;
