import React, { useEffect } from "react";
import {
  Container,
  Col,
  Row,
  Button,
  Modal,
  Alert,
  Form,
} from "react-bootstrap";
import { useState } from "react";
import "./HelpUs.css";
import { FaPen } from "react-icons/fa";

const HelpUs = () => {
  const [countries, setCountries] = useState([]);
  const [meals, setMeals] = useState([]);
  const [mealSelected, setMealSelected] = useState("");

  useEffect(() => {
    fetch(`https://www.themealdb.com/api/json/v1/1/list.php?a=list`)
      .then((response) => response.json())
      .then((data) => {
        var countriesData = [];
        data.meals.map((country) => {
          countriesData.push(country.strArea);
        });
        setCountries(countriesData);
      });
  }, []);

  const countrySelect = (country) => {
    fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?a=${country}`)
      .then((response) => response.json())
      .then((data) => {
        console.log(data.meals);
        const mealsArray = [];
        data.meals.map((meal) => {
          mealsArray.push(meal);
        });
        setMeals(mealsArray);
      });
  };

  return (
    <div>
      <div className="title">
        <Row>
          <Col xs={9}>
            <h2>
              Your opinion matters &nbsp;
              <FaPen />
            </h2>
          </Col>
        </Row>

        <hr />
      </div>
      <div>
        <Container>
          <Row>
            <Col xs={3}>
              <Form.Label>Select Country</Form.Label>
              <Form.Select
                aria-label="Default select example"
                onChange={(e) => {
                  countrySelect(e.target.value);
                }}
              >
                {countries.map((country) => (
                  <option value={country}>{country}</option>
                ))}
              </Form.Select>
            </Col>

            <Col xs={3}>
              <Form.Label>Select Meal</Form.Label>
              <Form.Select
                aria-label="Default select example"
                onChange={(e) => {
                  setMealSelected(e.target.value);
                }}
              >
                {meals.map((meals) => (
                  <option value={meals.strMeal}>{meals}</option>
                ))}
              </Form.Select>
            </Col>
          </Row>
        </Container>
        <br/>
        {mealSelected !== "" && (
          <Container>
            <h6>Selected Meal</h6>
            <br/>
            <img src=""></img>
          </Container>
        )}
      </div>
    </div>
  );
};

export default HelpUs;
