import React from "react";
import "./History.css";
import "./Card.css";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { dateFormater, addDays } from "./Functions/functions";
import { Table, Row, Col } from "react-bootstrap";

const History = () => {
  const [history, setHistory] = useState([]);

  const getHistory = () => {
    fetch("http://localhost:4000/getHistory")
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setHistory(json);
      });
  };

  useEffect(() => {
    getHistory();
  }, []);

  return (
    <div>
      <div className="title">
        <div class="container py-4">
          <header class="pb-3 mb-4 border-bottom">
            <a class="d-flex align-items-center text-dark text-decoration-none">
              <span class="fs-4">WineLovers Website - Previous Order</span>
            </a>
          </header>{" "}
        </div>
        {history.length !== 0 && (
          <>
            <Table striped bordered hover size="sm">
              <thead>
                <tr>
                  <th>Order Details</th>
                </tr>
              </thead>
              <tbody>
                {history.map((history) => (
                  <tr>
                    <td className="personal-details-div">
                      <div>
                        {history.Wines.map((wine) => (
                          <p>&#8226; {wine.wine.WineName}</p>
                        ))}
                      </div>
                    </td>
                    <td>
                      <div>
                        <p>{history.OrderDate.slice(0, 10)}</p>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </>
        )}
      </div>
    </div>
  );
};

export default History;
