import React from "react";
import "./FoodAndWine.css";
import { Tabs, Tab, Row, Col, Offcanvas, Button } from "react-bootstrap";
import { ImSpoonKnife } from "react-icons/im";
import { useState } from "react";
import BigCategories from "./FoodAndWine/BigCategories";
import DayType from "./FoodAndWine/DayType";
import SpecificFood from "./FoodAndWine/SpecificFood";
const FoodAndWine = () => {
  const [key, setKey] = useState("home");
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  return (
    <div>
      <div className="offcanvas">
        <Offcanvas show={show} onHide={handleClose}>
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>Select Food Category</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <hr />
            <h4>Big Categories</h4>
            <p>&#8226; &nbsp;Red Meat</p>
            <p>&#8226; &nbsp;White Meat</p>
            <p>&#8226; &nbsp;Fish</p>
            <p>&#8226; &nbsp;Cheece</p>
            <p>&#8226; &nbsp;Pasta</p>
            <p>&#8226; &nbsp;Pizza</p>
            <hr />
            <h4>Day Type</h4>
            <p>&#8226; &nbsp;Cheece</p>
            <p>&#8226; &nbsp;Pasta</p>
            <p>&#8226; &nbsp;Pizza</p>
          </Offcanvas.Body>
        </Offcanvas>
      </div>

      <div className="title">
        <div class="container py-4">
          <header class="pb-3 mb-4 border-bottom">
            <a class="d-flex align-items-center text-dark text-decoration-none">
              <span class="fs-4">
                <ImSpoonKnife className="initial-icons" />
                &nbsp; Food & Wine
              </span>
            </a>
          </header>{" "}
          <Button variant="danger" onClick={handleShow}>
            Select category
          </Button>
        </div>
      </div>
    </div>
  );
};

export default FoodAndWine;
