import React from "react";
import "./BigCategories.css";
import { Chart } from "react-google-charts";
import { useState, useEffect } from "react";
import { Col, Row } from "react-bootstrap";

const BigCategories = ({ choice }) => {
  const [results, setResults] = useState([]);

  const getBigCategoriesResults = () => {
    let choiceDetails = {
      choice: choice,
    };
    fetch("http://localhost:3000/bigCaregories", {
      method: "POST",
      body: JSON.stringify(choiceDetails),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setResults(json);
      });
  };

  useEffect(() => {
    getBigCategoriesResults();
  }, []);

  const data = [
    ["Task", "Hours per Day"],
    ["Work", 11],
    ["Eat", 2],
    ["Commute", 2],
    ["Watch TV", 2],
    ["Sleep", 7],
  ];

  const options = {
    title: "My Daily Activities",
  };

  return (
    <div>
      <div>
        <Row>
          <Col xs={4}>
            <Chart
              chartType="PieChart"
              data={data}
              options={options}
              width={"100%"}
              height={"300px"}
            />
          </Col>
          <Col xs={4}>
            <Chart
              chartType="PieChart"
              data={data}
              options={options}
              width={"100%"}
              height={"400px"}
            />
          </Col>
          <Col xs={4}>
            <Chart
              chartType="PieChart"
              data={data}
              options={options}
              width={"100%"}
              height={"400px"}
            />
          </Col>
        </Row>
      </div>
    </div>
  );
};

export default BigCategories;
