import React, { useEffect } from "react";
import BigCategories from "./HelpUsPages/BigCategories";
import TypeDay from "./HelpUsPages/TypeDay";
import SpecificFood from "./HelpUsPages/SpecificFood";
import { Col, Row, Tabs, Tab } from "react-bootstrap";
import { useState } from "react";
import "./HelpUs.css";
import { FaPen } from "react-icons/fa";
import { MdOutlineExpandLess, MdOutlineExpandMore } from "react-icons/md";

const HelpUs = () => {
  const [key, setKey] = useState("home");
  // ------------- drop Downs Values -----------------
  const [colorWines, setColorWines] = useState([]);
  const [typeWines, setTypeWines] = useState([]);
  const [yearWines, setYearWines] = useState([]);
  const [grapeWines, setGrapeWines] = useState([]);
  const [wineYears, setWineYears] = useState([]);

  const getWines = () => {
    fetch("https://winelovers.herokuapp.com/getWines")
      .then((res) => res.json())
      .then((json) => {
        var colors = [];
        var types = [];
        var years = [];
        var prices = [];
        var grapes = [];
        for (const key in json.wines) {
          colors.push(json.wines[key].wine.Color);
          types.push(json.wines[key].wine.Type);
          years.push(json.wines[key].wine.Year);
          prices.push(json.wines[key].wine.Price);
          grapes.push(json.wines[key].wine.Grapes);
        }

        colors = [...new Set(colors)];
        types = [...new Set(types)];
        years = [...new Set(years)];
        grapes = [...new Set(grapes)];

        setColorWines(colors);
        setTypeWines(types);
        setYearWines(years);
        setGrapeWines(grapes);
      });
  };

  useEffect(() => {
    getWines();
  }, []);

  return (
    <div>
      <div className="title">
        <Row>
          <Col xs={9}>
            <h2>
              Your opinion matters &nbsp;
              <FaPen />
            </h2>
          </Col>
        </Row>
        <hr />
        {/* <Tabs
          activeKey={key}
          onSelect={(k) => setKey(k)}
          transition={false}
          className="mb-6"
        >
          <Tab eventKey="home" title="Big Categories">
            <div className="tab-body">
              <br />
              <BigCategories
                colorWines={colorWines}
                typeWines={typeWines}
                grapeWines={grapeWines}
              />
            </div>
          </Tab>
          <Tab eventKey="profile" title="Day Type">
            <div className="tab-body">
              <br />
              <TypeDay
                colorWines={colorWines}
                typeWines={typeWines}
                grapeWines={grapeWines}
              />
            </div>
          </Tab>
          <Tab eventKey="contact" title="Specific Foods">
            <div className="tab-body">
              <br />
              <SpecificFood
                colorWines={colorWines}
                typeWines={typeWines}
                grapeWines={grapeWines}
              />
            </div>
          </Tab>
        </Tabs> */}
        <Row>
              <Col xs={deviceSize < 1000 ? 12 : 4}>
                <div
                  className="container-category"
                  onClick={() => {
                    setChoice("Red Meat");
                    handleShow();
                  }}
                >
                  <img
                    className="image-category"
                    src="https://media.istockphoto.com/photos/grilled-striploin-steak-picture-id535786572?k=20&m=535786572&s=612x612&w=0&h=WAOuIsIUQB7zVW23C6MX9y5QCyl6KLPL2eYcOcc_Qdk="
                  ></img>
                  <div className="text-block">
                    <h4>Red Meat</h4>
                  </div>
                </div>
                {deviceSize < 1000 && (
                  <>
                    <br />
                    <br />
                    <br />
                  </>
                )}
              </Col>
              <Col xs={deviceSize < 1000 ? 12 : 4}>
                <div
                  className="container-category"
                  onClick={() => {
                    setChoice("White Meat");
                    handleShow();
                  }}
                >
                  <img
                    className="image-category"
                    src="https://health.clevelandclinic.org/wp-content/uploads/sites/3/2020/09/CC_HE_1191933521Pork.jpg"
                  ></img>
                  <div className="text-block">
                    <h4>White Meat</h4>
                  </div>
                </div>
                {deviceSize < 1000 && (
                  <>
                    <br />
                    <br />
                    <br />
                  </>
                )}
              </Col>
              <Col xs={deviceSize < 1000 ? 12 : 4}>
                <div
                  className="container-category"
                  onClick={() => {
                    setChoice("Fish");
                    handleShow();
                  }}
                >
                  <img
                    className="image-category"
                    src="https://www.recipetineats.com/wp-content/uploads/2021/04/Whole-Baked-Fish.00_02_06_16.Still015.jpg"
                  ></img>
                  <div className="text-block">
                    <h4>Fish</h4>
                  </div>
                </div>
              </Col>
            </Row>
      </div>
    </div>
  );
};

export default HelpUs;
