import React from "react";
import "./New.css";
import { Col, Row } from "react-bootstrap";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
import { BsPlus } from "react-icons/bs";
import { useHistory } from "react-router-dom";
import { BiTimeFive } from "react-icons/bi";

const New = () => {
  // ----------- width size -----------------------
  const [deviceSize, changeDeviceSize] = useState(window.innerWidth);
  useEffect(() => {
    const resizeW = () => changeDeviceSize(window.innerWidth);

    window.addEventListener("resize", resizeW); // Update the width on resize
    return () => window.removeEventListener("resize", resizeW);
  });
  const cookies = new Cookies();
  var userEmail = cookies.get("email");
  let history = useHistory();

  //-------------------- Get news ----------------------------
  const getNews = () => {
    fetch("http://localhost:4000/getNews")
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
      });
  };

  useEffect(() => {
    //Runs only on the first render
  }, []);


  
  return (
    <div>
      <div className="title">
        <div class="container py-4">
          <header class="pb-3 mb-4 border-bottom">
            <a class="d-flex align-items-center text-dark text-decoration-none">
              <span class="fs-4">WineLovers Website</span>
            </a>
          </header>{" "}
        </div>

        <div className="news-container">
          {userEmail === "jimarass@hotmail.gr" && (
            <h2>
              <BsPlus
                onClick={() => {
                  history.push("/AddNew");
                }}
              />
            </h2>
          )}

          <Row>
            <Col xs={4}>
              <div>
                <img
                  className="image-new"
                  src="https://assets.vivino.com/KGkJVwh9x4ckZ35eMA5VrK1A"
                ></img>
                <p>
                  {" "}
                  <BiTimeFive /> {new Date().getFullYear()}
                  <h5>The Best Wines for Summer</h5>
                </p>
              </div>
            </Col>
            <Col xs={4}>
              <div>
                <img
                  className="image-new"
                  src="https://assets.vivino.com/KGkJVwh9x4ckZ35eMA5VrK1A"
                ></img>
                <p>
                  {" "}
                  <BiTimeFive /> {new Date().getFullYear()}
                  <h5>The Best Wines for Summer</h5>
                </p>
              </div>
            </Col>
            <Col xs={4}>
              <div>
                <img
                  className="image-new"
                  src="https://assets.vivino.com/KGkJVwh9x4ckZ35eMA5VrK1A"
                ></img>
                <p>
                  {" "}
                  <BiTimeFive /> {new Date().getFullYear()}
                  <h5>The Best Wines for Summer</h5>
                </p>
              </div>
            </Col>
          </Row>
        </div>
      </div>
    </div>
  );
};

export default New;
