import React from 'react'
import './NavBar.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faBars} from '@fortawesome/free-solid-svg-icons'
import { Link } from 'react-router-dom'
import { useState } from 'react'


const NavBar = () => {

  // --------------------NavBar Active-----------------
  const navArray = [] ;
  for (let i = 0; i < 3; i++) {
    navArray[i]='' ;
  }
  navArray[0] = 'active' ;
  const [activeNavBar, setActiveNavBar] = useState(navArray);

  const activeHandler = () =>{
    for (let i = 0; i < 3; i++) {
      navArray[i]='' ;
    }
    navArray[0] = 'active' ;
  const [activeNavBar, setActiveNavBar] = useState(navArray);
  }

    function myFunction() {
        var x = document.getElementById("myTopnav");
        if (x.className === "topnav") {
          x.className += " responsive";
        } else {
          x.className = "topnav";
        }
      }

    return (
        
            <div>
                <div className="topnav" id="myTopnav">
                    
                    <Link className={activeNavBar[0]} onClick={activeHandler} to="/" >Seminars</Link>
                    <Link onClick={activeHandler} to="/users">Users</Link>
                    <Link onClick={activeHandler} to="/teams">Teams</Link>
                    <Link onClick={activeHandler} to="/seminartype">Type</Link>
                    <a  className="icon" onClick={myFunction} >
                    <FontAwesomeIcon icon={faBars} />
                    </a>
                </div>


            </div>
        
    )
}

export default NavBar
