import React from "react";

const Login = () => {
  const isLogin = () => {
    let userDetails = {
      email: "jimarass@hotmail.gr",
      password: "123456",
    };
    fetch("http://localhost:4000/login", {
      method: "POST",
      body: JSON.stringify(userDetails),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        if(json.status === true)
        {
            alert("")
        }
      });
  };

  isLogin();

  return <div>Login</div>;
};

export default Login;
