    if(event.target.value.trim() === '')
    {
      setArray(users.slice(0,k));
      setRangeArray(users)
    }
    else{
      setSearchTerm(event.target.value);
      const results = users.filter(person =>
        person.first_name.trim().toUpperCase().includes(event.target.value)
      );
      if(results.length === 0 )
      {
        const results = users.filter(person =>
            person.first_name.trim().toUpperCase().includes(event.target.value)
          );
      }

      setArray(results.slice(0,k));
      setRangeArray(results)
    }}

     //--------------------- Pop Up -------------------------
     const popupCloseHandler = () => {

        setVisibility(false);

      };


    return (
        <div className = 'seminartable'> 
