                        {Array.map( (user) => (
                            <tr key = {user.id}>
                                <td onClick={() => nameHandler(users.first_name, users.id)} >{user.first_name}</td>
                                <td>{user.last_name}</td>
                                <td>{user.email}</td>
                                <td>{user.company}</td>
                                <td>{user.manager}</td>
                                
                            </tr>
                        ) )}
                         <tr>
                        <div className="pagination">             
                      { 
                          arr.map( (el) => (
                              <a  className={IsActive[el]} key={el} onClick={  () => {paginationHandler(el)} }  href="#">{el+1}</a>
                          ) )
                          }

                      
                      </div>
                      </tr> 
                      <tr>
                          <PopupUsers onClose={popupCloseHandler} show={visibility} title="Add A New User"> </PopupUsers>  
                      </tr>
                               
                    </thead>  
                </table>
                
               
        </div> 
    )
}

export default Users
