import React from "react";
import BootstrapTable from "react-bootstrap-table-next";
import { useState, useEffect } from "react";
import { Button, Container, Row, Col, Form } from "react-bootstrap";
import "./Wines.css";

const Wines = () => {
  const [wines, setWines] = useState([]);

  const getWines = () => {
    fetch("http://localhost:4000/getWines")
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setWines(json);
      });
  };
  useEffect(() => {
    getWines();
  }, []);

  console.log(wines);

  return (
    <div>
      <Container>
        {wines.length !== 0 && (
          <Row>
            <Col xs={3}></Col>
          </Row>
        )}
      </Container>
      {wines.length !== 0 && (
        <Container className="item">
          <div className="card card-body">
            <div className="media align-items-center align-items-lg-start text-center text-lg-left flex-column flex-lg-row">
              <div className="mr-2 mb-3 mb-lg-0">
                <img className="image" src={wines[0].ImageUrl} />
              </div>
              <div className="media-body">
                <h6 className="media-title font-weight-semibold">
                  <a href="/wines/info/{{this.wine._id}}" data-abc="true"></a>
                </h6>
                <ul className="list-inline list-inline-dotted mb-3 mb-lg-2">
                  <li className="list-inline-item">
                    <a href="#" className="text-muted" data-abc="true"></a>
                  </li>
                  <li className="list-inline-item">
                    <a href="#" className="text-muted" data-abc="true"></a>
                  </li>
                  <li className="list-inline-item">
                    <a href="#" className="text-muted" data-abc="true"></a>
                  </li>
                  <br />
                  <li className="list-inline-item">
                    <a href="#" className="text-muted" data-abc="true"></a>
                  </li>
                </ul>
                <p className="mb-3"></p>
                <ul className="list-inline list-inline-dotted mb-0">
                  <li className="list-inline-item">
                    {" "}
                    <strong>Winery by</strong> <a href="#" data-abc="true"></a>
                  </li>
                  <br />
                  <li className="list-inline-item">
                    {" "}
                    <strong>Grape : </strong>{" "}
                  </li>
                </ul>
              </div>
              <div className="mt-3 mt-lg-0 ml-lg-3 text-center">
                <h3 className="mb-0 font-weight-semibold"> â‚¬</h3>
                <div className="text-muted">
                  {" "}
                  <span className="fa fa-star checked"></span>{" "}
                </div>
                <div className="text-muted"> 0 reviews </div>
                <div className="text-muted"> </div>

                <form action="/auth/addToCard" method="POST">
                  <input name="wineId" type="text" hidden />
                  <input
                    name="amount"
                    placeholder="Choose Amount"
                    type="number"
                    min="0"
                    required
                  />{" "}
                  <br />
                  <button
                    type="submit"
                    className="btn btn-warning mt-4 text-white"
                  >
                    <i className="icon-cart-add mr-2"></i> Add to cart
                  </button>
                </form>
              </div>
            </div>
          </div>
        </Container>
      )}
    </div>
  );
};

export default Wines;
