import React from 'react'
import './Seminars.css'
import { useState , useEffect} from 'react'
import { useHistory } from 'react-router'

import CustomPopup from '../Components/CustomPopup'


const Seminars = () => {

  const [visibility, setVisibility] = useState(false);
    

    const seminars = [
        {id: 1 , training: "seminar1", organization: "Agile Actors", type: 12 },
        {id: 2 , training: "seminar2", organization: "OTE Academy", type: 24 },
        {id: 3 , training: "seminar3", organization: "Oracle", type: 18 },
        {id: 4 , training: "seminar4", organization: "Academy", type: 25 },
        {id: 5 , training: "seminar5", organization: "Oracle", type: 19},
        {id: 6 , training: "seminar6", organization: "Agile Actors", type: 12 },
        {id: 7 , training: "seminar7", organization: "Agile Actors", type: 12 },
        {id: 8 , training: "seminar8", organization: "Agile Actors", type: 12 },
        {id: 9 , training: "seminar9", organization: "Agile Actors", type: 12 },
        {id: 10 , training: "seminar10", organization: "Agile Actors", type: 12 },
        {id: 11 , training: "seminar2", organization: "OTE Academy", type: 24 },
        {id: 12, training: "seminar3", organization: "Oracle", type: 18 },
        {id: 13 , training: "seminar4", organization: "Academy", type: 25 },
        {id: 14 , training: "seminar5", organization: "Oracle", type: 19},
        {id: 15 , training: "seminar6", organization: "Agile Actors", type: 12 },
        {id: 16 , training: "seminar7", organization: "Agile Actors", type: 12 },
        {id: 17 , training: "seminar8", organization: "Agile Actors", type: 12 },
        {id: 18 , training: "seminar9", organization: "Agile Actors", type: 12 },
        {id: 19 , training: "seminar10", organization: "Agile Actors", type: 12 },
        {id: 20 , training: "seminar2", organization: "OTE Academy", type: 24 },
        {id: 21 , training: "seminar3", organization: "Oracle", type: 18 },
        {id: 22 , training: "seminar4", organization: "Academy", type: 25 },
        {id: 23 , training: "seminar5", organization: "Oracle", type: 19},
        {id: 24 , training: "seminar6", organization: "Agile Actors", type: 12 },
        {id: 25 , training: "seminar7", organization: "Agile Actors", type: 12 },
        {id: 26 , training: "seminar8", organization: "Agile Actors", type: 12 },
        {id: 27 , training: "seminar9", organization: "Agile Actors", type: 12 },
        {id: 28 , training: "seminar10", organization: "Agile Actors", type: 12 }
      ];

      let history = useHistory();

      let isActive = []; //------ Active in Pagination
      

      // ------------------ Calculate Range ------------------
      const k = 6;
      const range = seminars.length/k +1;
      
      for (let i = 0; i <range; i++) {
        isActive[i] = '' ;          
      }
      isActive[0] = 'active' ;

      const [IsActive , setIsActive] = useState(isActive) ;

      const arr = seminars.slice(0,range);
      const [Array , setArray] = useState(seminars.slice(0,k));
      

      for (let i = 0; i < arr.length; i++) {
          arr[i] = i;
          
      }
      


    // ------------------------PAGINATION HANDLER ---------------------------------

      const paginationHandler = (el) => {
          setArray(seminars.slice(k*el,k*el+k))


          for (let i = 0; i < range; i++) {
              isActive[i] = '' ;
            }

            isActive[el] = 'active'
           setIsActive(isActive); 
      }



    // ------------------------ OnClick Functions -------------------------------
      const nameHandler = (training, id) => {

          history.push(`/seminar/${id}`);
          console.log(training, id)
      }

      const emailHandler = (organization) => {
          
        console.log(organization)
    }

    // ------------------------------Search Function ------------------------
    const [searchTerm, setSearchTerm] = useState("");
    const [searchResults, setSearchResults] = useState([]);


    const EffectHandler = () => {
      useEffect(() => {
        const results = seminars.filter(person =>
          person.training.toLowerCase().includes(searchTerm)
        );
        setArray(results);
      }, [searchTerm]);
    }


    function myFunction(event) {

      setSearchTerm(event.target.value);
      
      const results = seminars.filter(person =>
        person.training.toLowerCase().includes(searchTerm)
      );
      setArray(results);
      console.log(A)
      
      

      }

      
      useEffect(() => {
        const results = seminars.filter(person =>
          person.training.toLowerCase().includes(searchTerm)
        );
       
      }, [searchTerm]);
      console.log("vsdf")
      console.log(Array)
      



      const popupCloseHandler = () => {

        setVisibility(false);

      };

                  

    return (
        <div className = 'seminartable'>
            <form className='example'>
                <input type="text" id="myInput"  onChange={myFunction} placeholder="Search for seminars.." name="search"/>

            </form>
          
                 <table id="myTable">
                    <thead>
                        <tr>
                            <th >Seminars</th>
                            <th >Organization</th>
                            <th >Type</th>
                            
                        </tr>

                        {Array.map( (seminar) => (
                            <tr key = {seminar.id}>
                                <td className={'pointer'} onClick={() => nameHandler(seminar.training, seminar.id)} >{seminar.training}</td>
                                <td className={'pointer'} onClick={() => emailHandler(seminar.organization)} >{seminar.organization}</td>
                                <td>{seminar.type}</td>
                                
                            </tr>
                        ) )}

                      <tr>
                        <div className="pagination">             
                      { 
                          arr.map( (el) => (
                              <a  className={IsActive[el]} key={el} onClick={  () => {paginationHandler(el)} }  href="#">{el+1}</a>
                          ) )
                          }

                      
                      </div>
                      </tr>        
                
                    </thead>
                    
                </table>

                <button className='buttonad' onClick={() => setVisibility(true)}>Add New Seminar </button>

                <CustomPopup onClose={popupCloseHandler} show={visibility} title="Add A New Seminar"> </CustomPopup>
                

                
                    
            
        </div>
    )
}

export default Seminars
