import React from "react";
import "./AboutUs.css";
const AboutUs = () => {
  return (
    <div>
      <div className="title">
        <div class="container py-4">
          <header class="pb-3 mb-4 border-bottom">
            <a class="d-flex align-items-center text-dark text-decoration-none">
              <span class="fs-4">WineLovers Website</span>
            </a>
          </header>{" "}
        </div>
        <div className="about-container">
          <h2>About WineLovers</h2>

          <hr className="hr-modify about" />
        </div>
        <div className="about-info">
          <p>
            &nbsp;&nbsp;&nbsp;&nbsp;<strong>WineLovers</strong> is a website created by{" "}
            <a href="https://www.linkedin.com/in/dimitris-siamplis-334b00177/">
              Dimitrios Siamplis
            </a>
            &nbsp; with the aim of realizing his diploma work, at{" "}
            <a href="https://www.upatras.gr/">University of Patras,Greece</a>
            &nbsp; in the department of{" "}
            <a href="https://www.upatras.gr/education/undergraduate-studies/school-of-engineering/department-of-electrical-and-computer-engineering/">
              Electrical & Computer Engineering
            </a>
            . Dimitris's professor{" "}
            <a href="http://www.ece.upatras.gr/index.php/el/faculty/navouris.html">
              Nikolaos Avouris
            </a>{" "}
            he is a good person with great knowledge of software
            technology, with an emphasis on Human-Computer Communication,
            human-computer interface design methodologies, Artificial
            Intelligence, Expert Systems, Distributed Intelligent Systems,
            Machine Learning techniques and the application of these techniques
            in complex problems in the field of Cooperative Learning,
            Environment, Education and Industry.
          </p>
          <p></p>
        </div>
      </div>
    </div>
  );
};

export default AboutUs;
