import React, { useEffect, useState } from "react";
import "./TypeDay.css";
import { Col, Row, Form, Button } from "react-bootstrap";
import { BsFillSuitHeartFill } from "react-icons/bs";
import { GiEasterEgg } from "react-icons/gi";
import { TbChristmasTree } from "react-icons/tb";

const TypeDay = ({ colorWines, typeWines, grapeWines }) => {
  const [deviceSize, changeDeviceSize] = useState(window.innerWidth);
  useEffect(() => {
    const resizeW = () => changeDeviceSize(window.innerWidth);

    window.addEventListener("resize", resizeW); // Update the width on resize
    return () => window.removeEventListener("resize", resizeW);
  });
  const [christmas, setChristmas] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [easter, setEaster] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [valentine, setValentine] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  return (
    <div>
      <div>
        <div className="helpUsTitleContent">
          <p>- Wine would you pair on : </p>

          <div className="helpUsTitleContent">
            <p>
              <strong>1.</strong> Christmas Day &nbsp;{" "}
              <TbChristmasTree className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Type</Form.Label>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setChristmas({
                      Type: e.target.value,
                      Color: christmas.Color,
                      Grape: christmas.Grape,
                      Year: christmas.Year,
                    });
                  }}
                >
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Color</Form.Label>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setChristmas({
                      Type: christmas.Type,
                      Color: e.target.value,
                      Grape: christmas.Grape,
                      Year: christmas.Year,
                    });
                  }}
                >
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Grape</Form.Label>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setChristmas({
                      Type: christmas.Type,
                      Color: christmas.Color,
                      Grape: christmas.Grape,
                      Year: e.target.value,
                    });
                  }}
                >
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Year Range</Form.Label>
                <Form.Select size="sm">
                  <option>Small select</option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>2.</strong> Easter Day &nbsp;{" "}
              <GiEasterEgg className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Type</Form.Label>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setEaster({
                      Type: e.target.value,
                      Color: easter.Color,
                      Grape: easter.Grape,
                      Year: easter.Year,
                    });
                  }}
                >
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Color</Form.Label>
                <Form.Select
                  size="sm"
                  onChange={(e) => {
                    setReadMeat({
                      Type: redMeat.Type,
                      Color: e.target.value,
                      Grape: redMeat.Grape,
                      Year: redMeat.Year,
                    });
                  }}
                >
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Grape</Form.Label>
                <Form.Select size="sm">
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Year Range</Form.Label>
                <Form.Select size="sm">
                  <option>Small select</option>
                </Form.Select>
              </Col>
            </Row>
          </div>

          <div className="helpUsTitleContent">
            <p>
              <strong>3.</strong> Valentine Day &nbsp;{" "}
              <BsFillSuitHeartFill className="reactIcon" />{" "}
            </p>

            <Row>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Type</Form.Label>
                <Form.Select size="sm">
                  {typeWines.map((type) => (
                    <option>{type}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Color</Form.Label>
                <Form.Select size="sm">
                  {colorWines.map((color) => (
                    <option>{color}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Grape</Form.Label>
                <Form.Select size="sm">
                  {grapeWines.map((grape) => (
                    <option>{grape}</option>
                  ))}
                </Form.Select>
              </Col>
              <Col xs={deviceSize < 600 ? 6 : 3}>
                <Form.Label>Year Range</Form.Label>
                <Form.Select size="sm">
                  <option>Small select</option>
                </Form.Select>
              </Col>
            </Row>
          </div>
        </div>
      </div>
      <br />
      <Row>
        <Col xs={1}>
          <Button variant="danger">Clear</Button>
        </Col>
        <Col xs={1}></Col>
        <Col xs={1}>
          <Button variant="success">Aply</Button>
        </Col>
      </Row>
    </div>
  );
};

export default TypeDay;
