import React from 'react'
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './Team.css'
import Spiner from '../Components/Spiner';
import '../Components/Spiner.css'
import { useState } from 'react';
import moment from "moment";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faSquare} from '@fortawesome/free-solid-svg-icons'


const Teams = () => {
    const [isLoading, setIsLoading] = useState(true)
    setTimeout( () => { setIsLoading(false) }, 500)
    
    
      
      const trackedDays = [ { start:"2021-12-12" , finish: "2021-12-17" } , { start:"2021-12-30" , finish: "2022-1-3" } ]
      const trackedDays1 = ["2021-12-25", "2021-12-27"];
      

      // ----------------RANGE ARRAYS THAT YOU WANT TO APPEAR---------------------
      const test = [];
      const vacation = [] ;
    

      // ----------------------- RANGE MAKER FUNCTION -----------------------------
    
    
    const rangeMaker = (startDate , finishDate) => {

        
        var currentDate = moment(startDate);
        var stopDate = moment(finishDate);
        while (currentDate <= stopDate) {
            test.push( moment(currentDate).format('YYYY-MM-DD') )
            currentDate = moment(currentDate).add(1, 'days');
        }    

    }

    console.log(test)
    for (let i = 0; i < trackedDays.length; i++) {
        rangeMaker(trackedDays[i].start , trackedDays[i].finish)
        
    }

    // ----------------------- CONVERT DATE TO D-M-Y -----------------------
    function convertDate(inputFormat) {
        function pad(s) { return (s < 10) ? '0' + s : s; }
        var d = new Date(inputFormat)
        return [d.getFullYear(), pad(d.getDate()), pad(d.getMonth()+1) ].join('-')
      }



    // ------------------------ On CLICK DAY -------------------------

    const doSomething = (value,event) =>{

        alert(convertDate(value) )
    }
    
   

    return (
        <div>
            <div className='boxes'>
            <FontAwesomeIcon className = 'boxV'   size= 'lg'   icon={faSquare}/> &nbsp; Vacation &nbsp; 
            <FontAwesomeIcon className = 'boxC'   size= 'lg'   icon={faSquare}/> &nbsp; Current Day &nbsp; 
            <FontAwesomeIcon className = 'boxS'   size= 'lg'   icon={faSquare}/> &nbsp; Stand By &nbsp; 
            </div>
            {!isLoading &&  <Calendar  className='calendar' onClickDay = {(value, event ) => doSomething(value,event) } tileClassName={ ({ date, view }) =>
            {

            
                
                if(view === "month" && test.includes( moment(date).format("YYYY-MM-DD"))    )
                {
                    return("vocation");
                }
                if(trackedDays1.includes( moment(date).format("YYYY-MM-DD")))
                {
                    return("activeDay");
                }
                else{
                    return(null)
                }
                
            } 

            } 
                />}
            
            {isLoading && <Spiner/>}

            
            
        </div>
    )
}

export default Teams
