import React from "react";
import { Button } from 'react-bootstrap';

const Login = () => {
  const isLogin = () => {
    let userDetails = {
      email: "jimarass@hotmail.gr",
      password: "123456",
    };
    fetch("http://localhost:4000/login", {
      method: "POST",
      body: JSON.stringify(userDetails),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        if(json.status === true)
        {
            alert("Successful Lofin")
        }
        else{
            alert("Error Login")
        }
      });
  };

  isLogin();

  return <div>
      <div className="login">

      </div>
  </div>;
};

export default Login;
