import React from "react";
import "./Card.css";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";

const Card = () => {
  const [totalPrice, setTotalPrice] = useState(0);

  const cookies = new Cookies();
  var userEmail = cookies.get("email");

  var cardDetails =
    cookies.get("card") === undefined ? [] : cookies.get("card").split("|");

  console.log(cardDetails);

  const [card, setCard] = useState(cardDetails);

  // -------- Delete from Card ----------
  const onDeletefromCard = (item) => {
    var cardCookies = cookies.get("card");
    console.log(cardCookies);

    var cardArray = cardCookies.split("|");
    // cardArray = cardArray.filter((word) => word !== item);
    // console.log(cardArray);

    var newCardArray = [];
    for (const key in cardArray) {
      if (key != cardArray.length - 1) {
        newCardArray.push(cardArray[key].shift + "|");
      } else {
        newCardArray.push(cardArray[key]);
      }
    }

    console.log(newCardArray.toString() );
    console.log(newCardArray.toString() , cardCookies);

    // cookies.set("card", newCardArray.toString(), { path: "/" });
    // console.log(cookies.get("card").split("|"))
    // setCard(cookies.get("card").split("|"));
    // console.log(cookies.get("card").split("|"));
  };

  return (
    <div>
      <div className="cardboth">
        <div className="product-details mr-2">
          <div className="d-flex flex-row align-items-center">
            <i className="fa fa-long-arrow-left"></i>
            <button
              //   style="padding: 0; border: none; background: none;"
              className="ml-2"
            >
              Continue Shopping
            </button>
          </div>
          <hr />
          <h6 className="mb-0">Shopping cart</h6>
          {card.length !== 0 && (
            <div>
              <div className="d-flex justify-content-between">
                <span>
                  You have <strong>{card.length} items</strong> in your cart
                </span>
                <strong>Total Price : {totalPrice} €</strong>
              </div>

              {card.map((item) => (
                <div className="d-flex justify-content-between align-items-center mt-3 p-2 items rounded">
                  <div className="d-flex flex-row">
                    <a href={`/wine/${item.split(",")[0]}`}>
                      <img
                        className="cardImage"
                        src={item.split(",")[6]}
                        width="40"
                      />
                    </a>
                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    <div className="ml-2">
                      <span className="font-weight-bold d-block">
                        {item.split(",")[1]}
                      </span>
                      <span className="spec">
                        {item.split(",")[4]} , {item.split(",")[3]}
                      </span>
                    </div>
                  </div>
                  <div className="d-flex flex-row align-items-center">
                    <span className="d-block">
                      {/* {formatDate(new Date(item.card.CardDate))}{" "} */}
                      &nbsp;&nbsp;&nbsp;
                    </span>{" "}
                    &nbsp; <span className="d-block">{item.split(",")[7]}</span>
                    <strong>&nbsp; x &nbsp;</strong>
                    <span className="d-block ml-5 font-weight-bold">
                      {item.split(",")[5]}
                      &nbsp; = &nbsp;
                    </span>
                    <span className="d-block ml-5 font-weight-bold">
                      <strong>
                        {item.split(",")[7] * item.split(",")[5]} &nbsp;€
                      </strong>
                      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                      <FontAwesomeIcon
                        className="deleteIcon"
                        onClick={() => {
                          onDeletefromCard(item);
                        }}
                        icon={faTrash}
                        size="1x"
                      />
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="col-md-2">
          <br />
          <br />
          Continue with Order
          <br />
          <div className="payment-info">
            Personal Details
            <div className="row">
              <div className="col-md-6">
                <label className="credit-card-label">Shipping location</label>
                {/* <input
                      type="text"
                      className="form-control credit-inputs"
                      placeholder="Atiki,Greece"
                    /> */}
              </div>
              <div className="col-md-6">
                <label className="credit-card-label">Zip Number</label>
                {/* <input
                      type="text"
                      className="form-control credit-inputs"
                      placeholder="Zip number"
                    /> */}
              </div>
              <br />
              <div className="col-md-6">
                <label className="credit-card-label">Address with Number</label>
                {/* <input
                      type="text"
                      className="form-control credit-inputs"
                      placeholder="Zip number"
                    /> */}
              </div>
            </div>
            <hr className="line" />
            Card Details
            <div>
              <label className="credit-card-label">Name on card</label>
              {/* <input
                    type="text"
                    className="form-control credit-inputs"
                    placeholder="Name"
                  /> */}
            </div>
            <div>
              <label className="credit-card-label">Card number</label>
              {/* <input
                    type="text"
                    className="form-control credit-inputs"
                    placeholder="0000 0000 0000 0000"
                  /> */}
            </div>
            <div className="row">
              <div className="col-md-6">
                <label className="credit-card-label">Date</label>
                {/* <input
                      type="text"
                      className="form-control credit-inputs"
                      placeholder="12/24"
                    /> */}
              </div>
              <div className="col-md-6">
                <label className="credit-card-label">CVV</label>
                {/* <input
                      type="text"
                      className="form-control credit-inputs"
                      placeholder="342"
                    /> */}
              </div>
            </div>
            <hr className="line" />
            Sumary
            <div className="d-flex justify-content-between information">
              <span>Subtotal</span>
              <span>$</span>
            </div>
            <div className="d-flex justify-content-between information">
              <span>Shipping</span>
              <span>$0.00</span>
            </div>
            <div className="d-flex justify-content-between information">
              <span>Total(Incl. taxes)</span>
              <span></span>
            </div>
            <form action="/auth/addtoHistory" method="POST">
              {/* <input
                    type="number"
                    name="totalPrice"
                    value="{{total_price}}"
                  /> */}
              <button
                className="btn btn-primary btn-block d-flex justify-content-between mt-3"
                type="submit"
              >
                <span>$</span>
                <span>
                  Checkout<i className="fa fa-long-arrow-right ml-1"></i>
                </span>
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
