import React from 'react'
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import ToolkitProvider, { Search } from 'react-bootstrap-table2-toolkit';
import { useHistory } from 'react-router';
import { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.css';
import 'react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css';

import './Users.css'




const Users = () => {

    const [visibility, setVisibility] = useState(false);
    const [isLoading, setIsLoading] = useState(true)

    setTimeout( () => { setIsLoading(false) }, 500);

   

    const users = [ 
    { id:1 , first_name: 'ΕΥΑΓΓΕΛΟΣ' , last_name: 'ΤΡΙΑΚΟΣΑΡΗΣ' , email: 'xetriakos@ote.gr' , company: 'COSMOTE' , manager: 'ΚΩΣΤΑΣ'}
    ,{ id:2 , first_name: 'ΧΡΙΣΤΟΦΟΡΟΣ' , last_name: 'ΤΡΙΑΝΤΑΦΥΛΛΟΥ' , email: 'chtriantaf@cosmote.gr' , company: 'COSMOTE' , manager: 'ΣΑΜΠΑΤΗ'}
