    ,{ id:4 , first_name: 'ΑΝΑΣΤΑΣΙΟΣ' , last_name: 'ΤΣΑΟΥΣΗΣ' , email: 'tsaousis@ote.gr' , company: 'COSMOTE' , manager: 'ΜΑΡΙΟΣ'}
    ,{ id:5 , first_name: 'ΚΩΝΣΤΑΝΤΙΝΟΣ' , last_name: 'ΤΣΙΤΣΙΚΑΣ' , email: 'tsitsikas@ote.gr' , company: 'COSMOTE' , manager: 'ΜΑΡΙΟΣ'}
    ,{ id:6 , first_name: 'ΓΙΩΡΓΙΟΣ' , last_name: 'ΦΟΥΡΛΑΣ' , email: 'gfourlas@ist.gr' , company: 'IST' , manager: 'ΤΑΚΗΣ'}
    ,{ id:7 , first_name: 'ΕΙΡΗΝΗ' , last_name: 'ΧΑΖΑΠΗ' , email: 'xazapi@ote.gr' , company: 'COSMOTE' , manager: 'ΚΩΣΤΑΣ'}
    ,{ id:8 , first_name: 'ΕΥΑΓΓΕΛΟΣ' , last_name: 'ΤΡΙΑΚΟΣΑΡΗΣ' , email: 'xetriakos@ote.gr' , company: 'COSMOTE' , manager: 'ΚΩΣΤΑΣ'}
    ,{ id:9 , first_name: 'ΧΡΙΣΤΟΦΟΡΟΣ' , last_name: 'ΤΡΙΑΝΤΑΦΥΛΛΟΥ' , email: 'chtriantaf@cosmote.gr' , company: 'COSMOTE' , manager: 'ΣΑΜΠΑΤΗ'}
    ,{ id:10 , first_name: 'ΙΩΑΝΝΗΣ' , last_name: 'ΤΣΑΛΙΚΗΣ' , email: 'gtsal@ote.gr' , company: 'COSMOTE' , manager: 'ΜΑΚΗΣ'}
    ,{ id:11, first_name: 'ΑΝΑΣΤΑΣΙΟΣ' , last_name: 'ΤΣΑΟΥΣΗΣ' , email: 'tsaousis@ote.gr' , company: 'COSMOTE' , manager: 'ΜΑΡΙΟΣ'}
    ,{ id:12, first_name: 'ΚΩΝΣΤΑΝΤΙΝΟΣ' , last_name: 'ΤΣΙΤΣΙΚΑΣ' , email: 'tsitsikas@ote.gr' , company: 'COSMOTE' , manager: 'ΜΑΡΙΟΣ'}
    ,{ id:13 , first_name: 'ΓΙΩΡΓΙΟΣ' , last_name: 'ΦΟΥΡΛΑΣ' , email: 'gfourlas@ist.gr' , company: 'IST' , manager: 'ΤΑΚΗΣ'}
    ,{ id:14 , first_name: 'ΕΙΡΗΝΗ' , last_name: 'ΧΑΖΑΠΗ' , email: 'xazapi@ote.gr' , company: 'COSMOTE' , manager: 'ΚΩΣΤΑΣ'}
    ,{ id:15 , first_name: 'ΕΥΑΓΓΕΛΟΣ' , last_name: 'ΤΡΙΑΚΟΣΑΡΗΣ' , email: 'xetriakos@ote.gr' , company: 'COSMOTE' , manager: 'ΚΩΣΤΑΣ'}
    ,{ id:16 , first_name: 'ΧΡΙΣΤΟΦΟΡΟΣ' , last_name: 'ΤΡΙΑΝΤΑΦΥΛΛΟΥ' , email: 'chtriantaf@cosmote.gr' , company: 'COSMOTE' , manager: 'ΣΑΜΠΑΤΗ'}
    ,{ id:17 , first_name: 'ΙΩΑΝΝΗΣ' , last_name: 'ΤΣΑΛΙΚΗΣ' , email: 'gtsal@ote.gr' , company: 'COSMOTE' , manager: 'ΜΑΚΗΣ'}
    ,{ id:18, first_name: 'ΑΝΑΣΤΑΣΙΟΣ' , last_name: 'ΤΣΑΟΥΣΗΣ' , email: 'tsaousis@ote.gr' , company: 'COSMOTE' , manager: 'ΜΑΡΙΟΣ'}
    ,{ id:19 , first_name: 'ΚΩΝΣΤΑΝΤΙΝΟΣ' , last_name: 'ΤΣΙΤΣΙΚΑΣ' , email: 'tsitsikas@ote.gr' , company: 'COSMOTE' , manager: 'ΜΑΡΙΟΣ'}
    ,{ id:20 , first_name: 'ΓΙΩΡΓΙΟΣ' , last_name: 'ΦΟΥΡΛΑΣ' , email: 'gfourlas@ist.gr' , company: 'IST' , manager: 'ΤΑΚΗΣ'}
    ,{ id:21 , first_name: 'ΕΙΡΗΝΗ' , last_name: 'ΧΑΖΑΠΗ' , email: 'xazapi@ote.gr' , company: 'COSMOTE' , manager: 'ΚΩΣΤΑΣ'}
    
]


const fullName = [];

for(const key in users)
{
  fullName.push({
    id: users[key].id,
    fullName : `${users[key].first_name} ${users[key].last_name}` ,
    email: users[key].email,
    company: users[key].company,
    manager: users[key].manager
    })
}

console.log(fullName)

let history = useHistory();
 
    
const columns = [
  {dataField: 'id', text:'Id', sort: true, hidden:true  },
  {dataField: 'fullName', text:'Full Name', sort: true, searchable: false,
  events: {
    onClick: (e, column, columnIndex, row, rowIndex) => {
      console.log(row);
      history.push(`/users/${row.id}`)
    }
  }

},
  
  {dataField: 'email', text:'Email', sort: true,  searchable: false },
  {dataField: 'manager', text:'Manager', sort: true,},
  {dataField: 'company', text:'Company', sort: true,  searchable: false },
  
  
]




const defaultSorted = [{
 dataField: 'id',
 order: 'asc'
}];


 


const pagination = paginationFactory({
 page: 1,
 sizePerPage: 5,
 lastPageText: '>>',
 firstPageText: '<<',
 nextPageText: '>',
 prePageText: '<',
 showTotal: true,
 alwaysShowAllBtns: true,
 onPageChange: function (page, sizePerPage) {
   console.log('page', page);
   console.log('sizePerPage', sizePerPage);
 },
 onSizePerPageChange: function (page, sizePerPage) {
   console.log('page', page);
   console.log('sizePerPage', sizePerPage);
 }
});

const { SearchBar,  ClearSearchButton } = Search;





return (
 <div className="App">
   <ToolkitProvider
     bootstrap4
     keyField='id'
     data={fullName}
     columns={columns}
     search

   >
     {
       props => (
         <div className='SearchBar'>
           
           <SearchBar {...props.searchProps} />
           <ClearSearchButton {...props.searchProps} />
           <hr />
           

           
           
           <BootstrapTable
            
             defaultSorted={defaultSorted}
             pagination={pagination}
             {...props.baseProps}
           />
         </div>
       )
     }
   </ToolkitProvider>

 </div>
)
}

export default Users
