import React from 'react'
import './Seminars.css'
import { useState} from 'react'
import { useHistory } from 'react-router'

const Seminars = () => {


    

    const seminars = [
        {id: 1 , training: "seminar1", organization: "Agile Actors", type: 12 },
        {id: 2 , training: "seminar2", organization: "OTE Academy", type: 24 },
        {id: 3 , training: "seminar3", organization: "Oracle", type: 18 },
        {id: 4 , training: "seminar4", organization: "Academy", type: 25 },
        {id: 5 , training: "seminar5", organization: "Oracle", type: 19},
        {id: 6 , training: "seminar6", organization: "Agile Actors", type: 12 },
        {id: 7 , training: "seminar7", organization: "Agile Actors", type: 12 },
        {id: 8 , training: "seminar8", organization: "Agile Actors", type: 12 },
        {id: 9 , training: "seminar9", organization: "Agile Actors", type: 12 },
        {id: 10 , training: "seminar10", organization: "Agile Actors", type: 12 },
        {id: 2 , training: "seminar2", organization: "OTE Academy", type: 24 },
        {id: 3 , training: "seminar3", organization: "Oracle", type: 18 },
        {id: 4 , training: "seminar4", organization: "Academy", type: 25 },
        {id: 5 , training: "seminar5", organization: "Oracle", type: 19},
        {id: 6 , training: "seminar6", organization: "Agile Actors", type: 12 },
        {id: 7 , training: "seminar7", organization: "Agile Actors", type: 12 },
        {id: 8 , training: "seminar8", organization: "Agile Actors", type: 12 },
        {id: 9 , training: "seminar9", organization: "Agile Actors", type: 12 },
        {id: 10 , training: "seminar10", organization: "Agile Actors", type: 12 },
        {id: 2 , training: "seminar2", organization: "OTE Academy", type: 24 },
        {id: 3 , training: "seminar3", organization: "Oracle", type: 18 },
        {id: 4 , training: "seminar4", organization: "Academy", type: 25 },
        {id: 5 , training: "seminar5", organization: "Oracle", type: 19},
        {id: 6 , training: "seminar6", organization: "Agile Actors", type: 12 },
        {id: 7 , training: "seminar7", organization: "Agile Actors", type: 12 },
        {id: 8 , training: "seminar8", organization: "Agile Actors", type: 12 },
        {id: 9 , training: "seminar9", organization: "Agile Actors", type: 12 },
        {id: 10 , training: "seminar10", organization: "Agile Actors", type: 12 }
      ];

      let history = useHistory();

      let isActive = []; //------ Active in Pagination
      

      // ------------------ Calculate Range ------------------
      const k = 6;
      const range = seminars.length/k +1;
      
      for (let i = 0; i <range; i++) {
        isActive[i] = '' ;          
      }
      isActive[0] = 'active' ;

      const [IsActive , setIsActive] = useState(isActive) ;

      const arr = seminars.slice(0,range);
      const [Array , setArray] = useState(seminars.slice(0,k));
      console.log(Array)

      for (let i = 0; i < arr.length; i++) {
          arr[i] = i;
          
      }
      console.log(arr)


    // ------------------------PAGINATION HANDLER ---------------------------------

      const paginationHandler = (el) => {
          setArray(seminars.slice(k*el,k*el+k))


          for (let i = 0; i < range; i++) {
              isActive[i] = '' ;
            }

            isActive[el] = 'active'
           setIsActive(isActive); 
      }



    
      const nameHandler = (training, id) => {

          history.push(`/seminar/${id}`);
          console.log(training, id)
      }

      const emailHandler = (organization) => {
          
        console.log(organization)
    }


    return (
        <div className = 'seminartable'>
            <input type="text" id="myInput" onkeyup="myFunction()" placeholder="Search for names.." title="Type in a name"/>
            <table id="myTable">
                    <thead>
                        <tr>
                            <th >Seminars</th>
                            <th >Organization</th>
                            <th >Type</th>
                            
                        </tr>

                        {Array.map( (seminar) => (
                            <tr key = {seminar.id}>
                                <td className={'pointer'} onClick={() => nameHandler(seminar.training, seminar.id)} >{seminar.training}</td>
                                <td className={'pointer'} onClick={() => emailHandler(seminar.organization)} >{seminar.organization}</td>
                                <td>{seminar.type}</td>
                                
                            </tr>
                        ) )}

                            
                
                    </thead>
                    
                </table>
                <div className="pagination">
                    
                                
                    { 
                        arr.map( (el) => (
                            <a className={IsActive[el]} key={el} onClick={  () => {paginationHandler(el)} }  href="#">{el+1}</a>
                        ) )
                        }

                    
                </div>
        </div>
    )
}

export default Seminars
