import React from 'react'
import { useState } from "react";
import { useHistory } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faAngleDoubleDown} from '@fortawesome/free-solid-svg-icons'
import {faAngleDoubleUp} from '@fortawesome/free-solid-svg-icons'
import './Filters.css'
import Pie from './Pie';
import Form from "react-bootstrap/Form";
import Tables from './Tables';


const Filters = () => {


    const [isActive , setIsActive] = useState(false)
    const [activeTable , setActiveTable] = useState(false)

// --------------- HIDE AND APPEAR FILTERS -----------------------
  const sidebarHandler = () => {
    if(isActive === true)
    {
      setIsActive(false)
    }
    else{
      setIsActive(true)
      
    }
  }


  // -------------------- SUMBIT FUNCTION ------------------------
  const submitHandler = (event) => {
    //event.preventdefault();
    setActiveTable(true);
    sidebarHandler();

  }


    return (
        <div>
            <div>
      <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/css/bootstrap.min.css" integrity="sha384-BVYiiSIFeK1dGmJRAkycuHAHRg32OmUcww7on3RYdg4Va+PmSTsz/K68vbdEjh4u" crossOrigin="anonymous"></link>

      <div className="container"> <br/><br/>
      <h2>O2B Proccess Fixed Mass</h2>
      <hr/>
      {!isActive && (
      <p className='text'><FontAwesomeIcon className = 'buttonplus'   size= '2x' onClick={sidebarHandler}    icon={faAngleDoubleDown}/>  Filters  </p>)}
      {isActive && (
      <p className='text'><FontAwesomeIcon className = 'buttonplus'   size= '2x' onClick={sidebarHandler}    icon={faAngleDoubleUp}/> Hide  Filters  </p> )}
      {isActive && ( 
	      <div  className="col-lg-12 col-md-12 col-sm-12 col-xs-12">

           <p>This is </p>

       </div>
       )}

    </div>
  </div> 

{activeTable && (
    <div> <Tables/> <Pie/>  </div>
    
   )}
        </div>
    )
}

export default Filters
