import { useEffect, useState } from "react";
import popupStyles from './popup-users.module.css';
import PropTypes from "prop-types";


const PopupUsers = (props) => {
    // --------------------DEFINE HOOKS ---------------------------
    const [show, setShow] = useState(false);
    



    const closeHandler = (e) => {
        setShow(false);
        props.onClose(false);
    };

    useEffect(() => {
        setShow(props.show);
    }, [props.show]);

    return (
        <div 
        style={{ visibility: show ? "visible" : "hidden", opacity: show ? "1" : "0"}}
        className={popupStyles.overlay}>
         <div className={popupStyles.popup}>
             <h4>{props.title}</h4>
             <span className={popupStyles.close} onClick={closeHandler}>&times;</span>

            <form className={popupStyles.formad}>
             <label className={popupStyles.labelad}>First Name: </label><br/>
            <input  className = {popupStyles.inputad} type="text" /><br/>

             <label className={popupStyles.labelad}>Last Name:<br/></label><br/>
             <input className = {popupStyles.inputad} type="text" /><br/>

             <label className={popupStyles.labelad}>Email:<br/></label><br/>
             <input className = {popupStyles.inputad} type="email" /><br/>

             <label className={popupStyles.labelad}>Company<br/></label><br/>
             <input className = {popupStyles.inputad} type="email" /><br/>

             <label className={popupStyles.labelad}>Email:<br/></label><br/>
             <input className = {popupStyles.inputad} type="email" /><br/>

             

            <button className={popupStyles.buttonad} type='submit' >Add Seminar</button>

            </form>

             <div className={popupStyles.content}>{props.children}</div>
         </div>
        </div>
    );

 }; 

 PopupUsers.propTypes = {
    title: PropTypes.string.isRequired,
    show: PropTypes.bool.isRequired,
    onClose: PropTypes.func.isRequired
  };
  

  export default PopupUsers;
