import React from "react";
import "./FoodAndWine.css";
import { Row, Col, Button, Modal } from "react-bootstrap";
import { ImSpoonKnife } from "react-icons/im";
import { useState, useEffect } from "react";
import BigCategories from "./FoodAndWine/BigCategories";
import DayType from "./FoodAndWine/DayType";
import SpecificFood from "./FoodAndWine/SpecificFood";
import { useHistory } from "react-router-dom";
const FoodAndWine = () => {
  let history = useHistory();
  const [choice, setChoice] = useState("");
  const [image, setImage] = useState("");
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    if (choice !== "") {
      if (choice === "Red Meat") {
        setImage(
          "https://media.istockphoto.com/photos/grilled-striploin-steak-picture-id535786572?k=20&m=535786572&s=612x612&w=0&h=WAOuIsIUQB7zVW23C6MX9y5QCyl6KLPL2eYcOcc_Qdk="
        );
      }
      if (choice === "White Meat") {
        setImage(
          "https://health.clevelandclinic.org/wp-content/uploads/sites/3/2020/09/CC_HE_1191933521Pork.jpg"
        );
      }
      if (choice === "Fish") {
        setImage(
          "https://www.recipetineats.com/wp-content/uploads/2021/04/Whole-Baked-Fish.00_02_06_16.Still015.jpg"
        );
      }
    }
  }, [choice]);
  return (
    <div>
      <div className="title">
        <Modal show={show} onHide={handleClose} size="xl">
          <Modal.Header closeButton>
            <Modal.Title>{choice}</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <img className="image" src={image}></img>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            {/* <Button variant="primary" onClick={handleClose}>
              Save Changes
            </Button> */}
          </Modal.Footer>
        </Modal>
        <div className="container py-4">
          <header className="pb-3 mb-4 border-bottom">
            <a className="d-flex align-items-center text-dark text-decoration-none">
              <span className="fs-4">
                <ImSpoonKnife className="initial-icons" />
                &nbsp; Food & Wine
              </span>
            </a>
          </header>{" "}
          <div>
            {/* <div className="image-food-wine">
              
            </div> */}

            <Row>
              <Col xs={4}>
                <div
                  className="container-category"
                  onClick={() => {
                    setChoice("Red Meat");
                    handleShow();
                  }}
                >
                  <img
                    className="image-category"
                    src="https://media.istockphoto.com/photos/grilled-striploin-steak-picture-id535786572?k=20&m=535786572&s=612x612&w=0&h=WAOuIsIUQB7zVW23C6MX9y5QCyl6KLPL2eYcOcc_Qdk="
                  ></img>
                  <div className="text-block">
                    <h4>Red Meat</h4>
                  </div>
                </div>
              </Col>
              <Col xs={4}>
                <div
                  className="container-category"
                  onClick={() => {
