import React from 'react'
import './Seminars.css'
import { useState} from 'react'
import { useHistory } from 'react-router'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faSearch} from '@fortawesome/free-solid-svg-icons'

const Seminars = () => {


    

    const seminars = [
        {id: 1 , training: "seminar1", organization: "Agile Actors", type: 12 },
        {id: 2 , training: "seminar2", organization: "OTE Academy", type: 24 },
        {id: 3 , training: "seminar3", organization: "Oracle", type: 18 },
        {id: 4 , training: "seminar4", organization: "Academy", type: 25 },
        {id: 5 , training: "seminar5", organization: "Oracle", type: 19},
        {id: 6 , training: "seminar6", organization: "Agile Actors", type: 12 },
        {id: 7 , training: "seminar7", organization: "Agile Actors", type: 12 },
        {id: 8 , training: "seminar8", organization: "Agile Actors", type: 12 },
        {id: 9 , training: "seminar9", organization: "Agile Actors", type: 12 },
        {id: 10 , training: "seminar10", organization: "Agile Actors", type: 12 },
        {id: 2 , training: "seminar2", organization: "OTE Academy", type: 24 },
        {id: 3 , training: "seminar3", organization: "Oracle", type: 18 },
        {id: 4 , training: "seminar4", organization: "Academy", type: 25 },
        {id: 5 , training: "seminar5", organization: "Oracle", type: 19},
        {id: 6 , training: "seminar6", organization: "Agile Actors", type: 12 },
        {id: 7 , training: "seminar7", organization: "Agile Actors", type: 12 },
        {id: 8 , training: "seminar8", organization: "Agile Actors", type: 12 },
        {id: 9 , training: "seminar9", organization: "Agile Actors", type: 12 },
        {id: 10 , training: "seminar10", organization: "Agile Actors", type: 12 },
        {id: 2 , training: "seminar2", organization: "OTE Academy", type: 24 },
        {id: 3 , training: "seminar3", organization: "Oracle", type: 18 },
        {id: 4 , training: "seminar4", organization: "Academy", type: 25 },
        {id: 5 , training: "seminar5", organization: "Oracle", type: 19},
        {id: 6 , training: "seminar6", organization: "Agile Actors", type: 12 },
        {id: 7 , training: "seminar7", organization: "Agile Actors", type: 12 },
        {id: 8 , training: "seminar8", organization: "Agile Actors", type: 12 },
        {id: 9 , training: "seminar9", organization: "Agile Actors", type: 12 },
        {id: 10 , training: "seminar10", organization: "Agile Actors", type: 12 }
      ];

      let history = useHistory();

      let isActive = []; //------ Active in Pagination
      

      // ------------------ Calculate Range ------------------
      const k = 6;
      const range = seminars.length/k +1;
      
      for (let i = 0; i <range; i++) {
        isActive[i] = '' ;          
      }
      isActive[0] = 'active' ;

      const [IsActive , setIsActive] = useState(isActive) ;

      const arr = seminars.slice(0,range);
      const [Array , setArray] = useState(seminars.slice(0,k));
      console.log(Array)

      for (let i = 0; i < arr.length; i++) {
          arr[i] = i;
          
      }
      console.log(arr)


    // ------------------------PAGINATION HANDLER ---------------------------------

      const paginationHandler = (el) => {
          setArray(seminars.slice(k*el,k*el+k))


          for (let i = 0; i < range; i++) {
              isActive[i] = '' ;
            }

            isActive[el] = 'active'
           setIsActive(isActive); 
      }



    
      const nameHandler = (training, id) => {

          history.push(`/seminar/${id}`);
          console.log(training, id)
      }

      const emailHandler = (organization) => {
          
        console.log(organization)
    }

    // ------------------------------Search Function ------------------------
    function myFunction() {
        var input, filter, table, tr, td, i, txtValue;
        input = document.getElementById("myInput");
        filter = input.value.toUpperCase();
        table = document.getElementById("myTable");
        tr = table.getElementsByTagName("tr");
        for (i = 0; i < tr.length; i++) {
          td = tr[i].getElementsByTagName("td")[0];
          if (td) {
            txtValue = td.textContent || td.innerText;
            if (txtValue.toUpperCase().indexOf(filter) > -1) {
              tr[i].style.display = "";
            } else {
              tr[i].style.display = "none";
            }
          }       
        }
      }



                    // Get the modal
                var modal = document.getElementById("myModal");

                // Get the button that opens the modal
                var btn = document.getElementById("myBtn");

                // Get the <span> element that closes the modal
                var span = document.getElementsByClassName("close")[0];

                // When the user clicks the button, open the modal 
                btn.onclick = function() {
                modal.style.display = "block";
                }

                // When the user clicks on <span> (x), close the modal
                span.onclick = function() {
                modal.style.display = "none";
                }

                // When the user clicks anywhere outside of the modal, close it
                window.onclick = function(event) {
                if (event.target == modal) {
                    modal.style.display = "none";
                }
                }

    return (
        <div className = 'seminartable'>
            <form className='example'>
                <input type="text" id="myInput"  onChange={myFunction} placeholder="Search for seminars.." name="search"/>

            </form>
          
                 <table id="myTable">
                    <thead>
                        <tr>
                            <th >Seminars</th>
                            <th >Organization</th>
                            <th >Type</th>
                            
                        </tr>

                        {Array.map( (seminar) => (
                            <tr key = {seminar.id}>
                                <td className={'pointer'} onClick={() => nameHandler(seminar.training, seminar.id)} >{seminar.training}</td>
                                <td className={'pointer'} onClick={() => emailHandler(seminar.organization)} >{seminar.organization}</td>
                                <td>{seminar.type}</td>
                                
                            </tr>
                        ) )}

                            
                
                    </thead>
                    
                </table>
                
                <div className="pagination">
                    
                                
                    { 
                        arr.map( (el) => (
                            <a className={IsActive[el]} key={el} onClick={  () => {paginationHandler(el)} }  href="#">{el+1}</a>
                        ) )
                        }

                    
                </div>
                <button class="button button1">Add New Seminar</button>
                <div id="myModal" class="modal">

                    
                    <div class="modal-content">
                        <span class="close">&times;</span>
                        <p>Some text in the Modal..</p>
                    </div>

</div>
        </div>
    )
}

export default Seminars
