import React, { useEffect, useState } from "react";
import "./TypeDay.css";
import { Col, Row, Form, Button } from "react-bootstrap";
import { BsFillSuitHeartFill } from "react-icons/bs";
import { GiEasterEgg } from "react-icons/gi";
import { TbChristmasTree } from "react-icons/tb";

const TypeDay = ({ colorWines, typeWines, grapeWines }) => {
  const [deviceSize, changeDeviceSize] = useState(window.innerWidth);
  useEffect(() => {
    const resizeW = () => changeDeviceSize(window.innerWidth);

    window.addEventListener("resize", resizeW); // Update the width on resize
    return () => window.removeEventListener("resize", resizeW);
  });

  const [isEnables, setIsEnabled] = useState(false);

  const [christmas, setChristmas] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [easter, setEaster] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const [valentine, setValentine] = useState({
    Type: "",
    Color: "",
    Grape: "",
    Year: "",
  });
  const wineYears = [
    "1950 or oldest",
    "1950-1980",
    "1980-2000",
    "2000-2010",
    "2010-2020",
    "2020-present",
  ];

  const onClearHandler = () => {
    const emptyData = {
      Type: "",
      Color: "",
      Grape: "",
      Year: "",
    };
    setChristmas(emptyData);
    setEaster(emptyData);
    setValentine(emptyData);
  };

  useEffect(() => {
    const isValidChristmas = [christmas].every(
      (item) =>
        item.Type === "" ||
        item.Color === "" ||
        item.Grape === "" ||
        item.Year === ""
    );
    const isValidEaster = [easter].every(
      (item) =>
        item.Type === "" ||
        item.Color === "" ||
        item.Grape === "" ||
        item.Year === ""
    );
    const isValidValentine = [valentine].every(
      (item) =>
        item.Type === "" ||
        item.Color === "" ||
        item.Grape === "" ||
        item.Year === ""
    );
    if (!isValidChristmas && !isValidEaster && !isValidValentine){
      
